css:
@-moz-document domain("nextbike.pl") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #0054A6 #1c1c1c;
}
/*STRONA-GLOWNA*/
.menu ul li a{
    color: #fff;
}
[class*=" icon-"], [class^="icon-"]{
    color: #fff;
}
header .login{
    background: transparent;
}
header .lang ul li{
    background: #1c1c1c;
    border: 1px solid #fff;
}
header .lang ul li a{
    color: #fff;
}
.menu ul li.hasSub > ul, .menu ul li.menu-item-has-children > ul{
    background: #1c1c1c;
}
section.clear{
    background: #1c1c1c;
}
.clear p{
    color: #fff;
}
section.banner h2{
    color: #fff;
}
.owl-carousel .owl-item img{
    background: #fff;
}
.news{
    background: #1c1c1c;
}
/*STRONA-GLOWNA*/
/*KONTAKT*/
.contactBox{
    background: #1c1c1c;
}
.contactBoxSingle .wrapper{
    background: #1c1c1c;
    border: 3px solid #fff;
}
.contactBoxSingle h3{
    color: #fff;
}
.contactBoxSingle p{
    color: #fff;
}
.form-group input{
    background: #1c1c1c !important;
    color: #fff !important;
}
.selectize-control.single .selectize-input{
    background: #1c1c1c;
}
.selectize-control.single .selectize-input.input-active, .sective-input{
    background: #1c1c1c;
    display: block
}
.caldera-grid .form-control{
    border: 0;
}
.contact .contactForm .caldera-grid div{
    background: #1c1c1c;
    color: #fff;
}
.contact .contactForm .caldera-grid .option.active{
    color: #0054A6;
}
.contact .contactForm .caldera-grid textarea{
    background: #1c1c1c;
    color: #fff;
}
.contact .contactForm .caldera-grid textarea:focus{
    color: #fff;
}
/*KONTAKT*/
/*ARTYKUL*/
section.clear .text p:not(.btn){
    color: #fff;
}
/*ARTYKUL*/
/*FOOTER*/
footer .footMenu:after{
    content: "Dark theme by Maciejozaman";
    color: #fff;
}
/*FOOTER*/
}
