css:
@-moz-document domain("d3.ru") {
body,
.l-header,
.l-content,
.react .w-app,
.react .s-footer,
.b-post_header .l-header-base,
.space_page .l-header-base,
.l-adv_page .l-header-base,
.b-new_post_footer,
.b-new_post_domain,
.b-side_menu_item_bg,
.b-side_menu_item_arrow,
.l-footer,
.b-textarea_editor,
.b-post_page,
.b-top_panel-content,
.b-form_submit {
    background-color: #383838;
    color: #bdbdbd;
    border: 0;
    background-image: none!important;
}
.b-promo {display: none!important;}
.react .b-comment_unread_true.b-comment_mode_default.b-comment_folded_false>.b-comment__body {
    background-color: #242424;
    border: #4e4e4e 1px solid;
    border-radius: 0px;
}
.react .w-app__content, .react .w-app__header {
    margin: 0 0 0 60px!important;
}
.react .w-app__header {
    position: fixed;
    top: 0;
    background-color: #383838;
}
.w-app {
    margin-top: 50px!important;
}
.w-app_fixed_true .w-app__content {
    filter:blur(3px);
    -webkit-filter:blur(3px);
}
.react,
.react .b-rating_vote-sign_zero .b-rating__value,
.b-post_subtitle .b-article_text .b-placeholder {
    color: #bdbdbd;
    fill: none;
}
.l-header {
    box-shadow: none;
}
.b-menu_link_text {
    border: 0;
}
input,
.b-article_text,
.i-form_textarea_white, .i-form_textarea {
    background-color: #383838!important;
    border: #4e4e4e 1px solid!important;
    color: #bdbdbd!important;
    border-radius: 0px!important;
    background-image: none!important;
}
.react .b-wysiwyg__editor,
.react .b-wysiwyg__toolbar,
.i-form_textarea_pure {
    background-color: #383838;
    color: #bdbdbd;
    border-radius: 0px;
    border-color: #4e4e4e;
}
.b-side_menu_item-separated:after {
    border-color: #4e4e4e;
}
.react .p-post-list__post,
.react .p-favourite-list__post {
    margin-top: 20px;
}
.react .p-post-item__line,
.b-tags_link {
    background: #383838;
}
.b-menu_link {
    border-color: #4e4e4e;
    background-color: #383838;
}
.react .b-feed-message__container,
.react .b-post-cut,
.react .b-base-sidebar__post-list,
.react .b-search,
.react .b-sidebar-footer__fixed-container,
.react .b-button_mode_default.b-button_color_snowgrey,
.react .b-sidebar-footer__movable-container,
.react .b-sidebar-footer__section_name_personal,
.react .b-sidebar-domain,
.react .s-menu__popup,
.react .s-menu__cut,
.react .s-menu__domain-list,
.react .p-post-item__layout,
.react .p-post-item__sidebar,
.react .p-post-item__post-list,
.react .b-button,
.react .b-domain-info,
.react .b-tag,
.react .b-select__option:hover,
.b-new_post,
.b-panel-menu,
.b-post_header_content_wrapper,
.b-post_header,
.b-header_expand_top_panel.js-active,
.react .s-domain-toolbar,
#overlay,
.react .b-form-image,
.react .b-alert_centered_true,
.mine .vote_result,
.b-votes_popup, .b-weights_popup,
.b-user_cover-bg {
    background: #404040!important;
    border-radius: 0px;
    border-color: #4e4e4e!important;
    color: #bdbdbd!important;
}
.b-votes_popup, .b-weights_popup {
    box-shadow: none;
}
.b-dude_note em, .b-header_nav .b-header_nav_button.b-header_nav_button__create .b-button_caption {
    color: #bdbdbd;
}
#spinner {
    border-color: #4e4e4e!important;
}
.react .s-domain-toolbar {
    border: #4e4e4e 1px solid;
}
.react .b-user-popup,
.react .b-domain-popup,
.react .b-notification-popup,
.react .b-notification-popup__toolbar,
.react .b-notification,
.l-base_domain .b-header_nav_button__active,
.react .b-select__menu {
    background: #383838!important;
    border-radius: 0px;
    border-color: #4e4e4e!important;
}
.post {
    background-color: #404040;
    border: #4e4e4e 1px solid;
    margin-bottom: 20px;
}
.react .b-sidebar-post,
.react .b-sidebar-footer__section_name_additional,
.react .b-sidebar-footer__section_name_additional,
.react .b-sidebar-footer__section_name_mail,
.react .b-sidebar-footer__section_name_personal {
    border-bottom: #4e4e4e 1px solid;
}
.post .dt a:visited,
.react .b-link_color_black,
.react .b-radio_mode_tab,
.react .b-user-login_color_black,
.react .b-karma_vote-count_0 .b-karma__value,
a {
    color: #d3ac54!important;
}
a:hover,
.react .b-user-login_color_black:hover,
.react .b-radio_mode_tab:hover,
.react .b-link_color_black:hover {
    color: #E39214!important;
}
.react .b-radio-group_mode_tab .b-radio-group__underline {
    background: #d3ac54;
}
#spinner::before {
    border-color: #d3ac54 transparent #d3ac54 #d3ac54!important;
    opacity: .25;
}
#spinner::after {
    border-color: transparent #d3ac54 transparent transparent!important;
    opacity: .75;
}
}
