css:
@-moz-document domain("forums.opera.com") {
body {
    font-family: basis_grotesque, -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
    font-size: 12px;
    line-height: 1.42857143;
    color: #ffffff;
    background-color: #333333 !important;
}
.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
    font-family: inherit;
    font-weight: 700;
    line-height: 1.1;
    color: #ffffff;
}
.topic .posts .content {
    padding: 20px 20px 50px;
    margin-top: -52px;
    margin-left: 64px;
    -webkit-transition: .75s ease-in-out border-color;
    transition: .75s ease-in-out border-color;
    background-color: #424242;
    border-radius: 0;
    border: 1px solid #424242;
}
.categories>li .card .post-content, .category>ul>li .card .post-content {
    margin-top: 10px;
    color: #ffffff !important;
    font-size: 13px;
    letter-spacing: 0;
}
.categories>li .category-details, .category>ul>li .category-details {
    padding: 0;
    background-color: #424242;
    border-radius: 0;
    border: 1px solid #424242;
}
.categories>li .content h2 a, .category>ul>li .content h2 a {
    color: #fff;
    font-weight: 700;
    -webkit-transition: color .15s ease-in;
    transition: color .15s ease-in;
}
.stats {
    font-size: 18px;
    font-weight: 700;
    padding: 17px 0;
    margin-top: 0;
    color: #fff;
}
.navbar-default {
    background-color: #4e4e4e;
    border-color: #333;
}
.categories .description, .category .description, .subcategory .description {
    font-size: 1.3rem;
    font-weight: 400;
    line-height: 1.2;
    color: #c1c1c1;
    margin-top: 5px;
    min-height: 25px;
}
.hf__footer {
    background: #424242;
    padding: 20px;
    position: relative;
    z-index: 1;
    margin-top: 20px;
    box-shadow: inset 1px 1px 0 #353535;
}
label {
    color: #ffffff;
    margin-bottom: 7px;
}
.hf a {
    text-decoration: none;
    color: #bbbbbb;
    -webkit-transition: color .25s ease-in-out;
    transition: color .25s ease-in-out;
}
.tag-item {
    padding: .4em .8em;
    font-size: 12px;
    text-transform: uppercase;
    color: #999;
    white-space: nowrap;
    border-radius: 0;
    background-color: #5a5a5a;
    margin-bottom: 10px;
    display: inline-block;
    letter-spacing: 2px;
    -webkit-transition: .2s ease-in-out all;
    transition: .2s ease-in-out all;
}
.category>ul>li .tag-list .tag {
    text-transform: uppercase;
    font-size: 10px;
    background: #696969;
    padding: 5px;
    white-space: nowrap;
    border-radius: 3px;
    letter-spacing: 1px;
}
.categories>li .box-footer a, .category>ul>li .box-footer a {
    white-space: nowrap;
    display: inline-block;
    margin-right: 10px;
    color: #ffffff;
    -webkit-transition: color .15s ease-in;
    transition: color .15s ease-in;
}
.custom-page {
    background-color: #424242;
    border-radius: 0;
    border: 1px solid #424242;
    padding: 23px 20px;
}
element.style {
    background-color: #5f5f5f;
}
element.style {
}
blockquote {
    padding: 10px 20px;
    margin: 0 0 20px;
    font-size: 17.5px;
    border-left: 5px solid #e6e6e6;
}
* {
    box-sizing: border-box;
}
user agent stylesheet blockquote {
    display: block;
    margin-block-start: 1em;
    margin-block-end: 1em;
    margin-inline-start: 40px;
    margin-inline-end: 40px;
}
.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    padding: 8px;
    line-height: 1.42857143;
    vertical-align: top;
    border-top: 1px solid #ddd;
}
table {
    border-collapse: collapse;
    border-spacing: 0;
}
user agent stylesheet table {
    border-collapse: separate;
    border-spacing: 2px;
}
body {
    font-family: basis_grotesque, -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
    font-size: 12px;
    line-height: 1.42857143;
    color: #ffffff;
    background-color: #333333 !important;
}
.topic .post-bar .topic-main-buttons {
    background-color: #424242;
    border-radius: 0;
    position: relative;
    z-index: 1;
}
.topic .post-bar .topic-main-buttons {
    border: 1px solid #424242;
    padding: 20px;
}
.form-control {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 10px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #737373;
    background-color: #4a4a4a;
    background-image: none;
    border: 1px solid #ccc;
    border-radius: 0;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075);
    -webkit-transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
}
}
