css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.advancedanime.com") {

body {
background: url(http://i41.tinypic.com/30cshfq.jpg) !important;
}
a {
color: #888 !important;
}
a:hover,  a:visited {
color: white !important;
}
td a {
color: white !important;
}
.section_header {
padding: 10px !important; 
color: white !important;
border: 0px !important;
border-top: 2px solid #0c0c0c !important;
}
.content_border { 
background: #1c1c1c !important;
border: 1px solid #2c2c2c !important;
}
.info {
border: 0px !important;
}
.sub_header {
border-top: 1px dashed #0c0c0c !important;
border-bottom: 1px dashed #0c0c0c !important;
}
td {
background: #2c2c2c !important;
color: white !important;
}

td span {
color: gray !important;
}
td span:hover {
color: white !important;
}
.info_b {
background: #1c1c1c !important;
border: 1px solid #2c2c2c !important;
}
.info_b a {
color: white !important;
}
.options {
background: transparent !important;
border: 0 !important;
border-left: 3px solid white !important;
padding-left: 15px !important;
font-size: 10px !important;
color: #888 !important;
}
#header_wrapper {
background: url(http://i41.tinypic.com/30cshfq.jpg) !important;
width: 920px !important;
margin-right: auto;
margin-left: auto;
}
#header_wrapper_bg {
background: url(http://i41.tinypic.com/30cshfq.jpg) !important;
}
#ubar_wrapper {
background: #1c1c1c !important;
border: 1px solid #2c2c2c !important;
-moz-border-radius: 7px !important;
}
#ubar_inner a {
color: white !important;
}
#tlm_wrapper {
height: 29px !important;
background: #1c1c1c !important;
border: 1px solid #2c2c2c !important;
border-bottom: 0px !important;
-moz-border-radius: 7px !important;
}
ul#tlm {
background: #1c1c1c !important;
border: 1px solid #2c2c2c !important;
-moz-border-radius: 7px !important;
color: white !important;
}
ul#tlm a.active {
background: #0c0c0c url(http://i43.tinypic.com/idf4b6.jpg) !important;
color: white !important;
}
ul#tlm a:hover {
background: #0c0c0c url(http://i43.tinypic.com/idf4b6.jpg) !important;
color: white !important;
}
#logo {
background: url(http://i39.tinypic.com/1zxa79t.png) center no-repeat !important;
width: 421px !important;
}
.footer {
background: #1c1c1c !important;
border: 1px solid #2c2c2c !important;
}
.footer a {
color: white !important;
}
}
