    body {
        background: #23232d;
         font-family: Nunito !important;
        font-weight: 300;
    }

    #main .menu {
        margin-left: 0;
    }

    #wrapper {
        background: #23232d;
        color: #dfdfdf;
        width: 90vw;
    }

    #content {
        width: 100%;
    }

    #oferta {
        background: #2c2c36;
        color: #dfdfdf;
        font-weight: 200;
        font-family: Nunito !important;
        font-size: 14px;
        position: relative;
        overflow: hidden;
    }

    b[style="font-size: 20px;"],
    #header, 
    #nav {
        display: none;
    }

    #oferta b {
        font-weight: 300;
    }

    #content h1 {
        color: #dfdfdf;
        font-weight: 200 !important;
        font-family: Nunito !important;
    }

    #oferta > table[width="100%"]:not(:nth-of-type(2)) {
        display: none;
    }

    #oferta > table[width="100%"] tr td {
        padding-top: 10px;  
        padding-bottom: 10px;
    }

    input {
        background:  #23232d !important;
        color: #dfdfdf;
        font-weight: 300 !important;
        border: 1px solid #dfdfdf;
        font-family: Nunito !important;
        outline: 1px solid rgba(0, 0, 0, 0);
        font-size: 16px !important;
    }

    input[type="text"] {
     padding-left: 5px;
        padding-right: 5px;
        font-weight: 200 !important;
    }

    div#oferta > a {
        color: transparent;
    }

    div#oferta > a:before {
        content: "Modyfikacja: CNK";
        color: white;
        position: absolute;
        left: 259px;
        background: #2c2c36;
        width: 100%;
    }

    font[color="blue"] {
        color: white;
        font-weight: 300;
    }

    font[color="blue"] b {
        font-weight: 300 !important;
    }

    th {
        font-weight: 200;
        font-size: 12px;
        white-space: none;
    }

    th br {content: ""}
    th br:after {content: "\00a0"}

    td[align="center"]>img {
        border-radius: 50%;
        position: relative;
    /*      vertical-align: middle; */
        width: 11px;
        height: 11px;
    }

    td[style="background=#ffffff;"] {
     font-size: 0;
    }

    td[align="right"] {
     text-align: left;   
    }

    td[align="center"] {
     text-align: left;   
    }

    td[style="background=#ffffff;"] b {
    /*     color: blue; */
        font-size: 14px;
        position: absolute;
        left: 20px;
    /*     float: left; */
    }

    #footer {
        display: none;
    }

    frame[name="dol"] {
        display: none !important;
        height: 0 !important;
        opacity: 0 !important;
        padding: 0 !important;
        margin: 0 !important;
        resize: unset !important;
        overflow: hidden;
    }
}
