css:
@-moz-document domain("kogama.com")
{
  body
  {
    font-family: Comic Sans MS;
  }

  #content-container
  {
    background-image: url(https://i.imgur.com/rOw0agT.jpg);
  }

  .input-fill
  {
    background-image: linear-gradient(#f2f2f2, #c7c7c7);
  }

  .feed-header
  {
    background-color: white;
  }

  .feed-item
  {
    background-color: white;
  }

  .feed-comments
  {
    background-color: white;
  }

  .item.status_updated, 
.item.wall_post, 
.item.marketplace_buy, 
.item.username_updated, 
.sections-container, 
.exit, .container, 
.emote-list, 
.item.username_updated, 
.feed-item
  {
    background-color: white !important;
  }

  .pure-button.pure-button-primary.button-fill
  {
    background-color: #e8e8e8 !important;
  }

  .pure-button.pure-pure-button-small.submit-comment.pure-button-primary
  {
    background-color: #e8e8e8 !important;
  }

  .username
  {
    color: white;
  }

  ._1Yhgq
  {
    background-image: linear-gradient(#4a4949,white) !important;
  }

  #profile-extended
  {
    background-image: linear-gradient(gray,white) !important;
  }

  .authenticated
  {
    background-color: #737272;
    color: black;
  }
}
