css:
@-moz-document url-prefix("https://lowcygier.pl") {
body {
    background-color: #182433;
    color: white;
}

.main-container {
    background-color: #182433;
}
.content-column .entry {
    background-color: #17324c;
    color: white;
}

.post .nc-main .post-title a {
    color: white;
}

.discount-code {
    background: #182433;
}

.post .nc-meta,
.post .nc-submeta {
    color: #6c9ae2;
}

.pagination-widget ul a,
.pagination-widget ul span {
    background: #6569a5;
}

.sidebar-widget h3 a {
    color: #6c9ae2;
}

.post-widget .nc-meta a {
    background: #6569a5;
    color: white;
}

body.dark {
    color: white;
}

.post-navigation {
    color: #6c9ae2;
}

.breadcrumbs-widget li:last-child a {
    color: #6c9ae2;
}
.breadcrumbs-widget a {
    color: #6c9ae2;
}

.search-results-header-widget h1 {
    color: white;
}

.table {
    color: white;
}

.wp-caption {
    background-color: #0d1b2f;
}

.table,
figure.wp-block-table {
    color: white;
}
}

@-moz-document url-prefix("https://bazar.lowcygier.pl") {
body {
    background-color: #182433;
    color: white;
}
.information {
    background: #17324c;
    color: white;
}
.navbar-inverse {
    background-color: #0e151d;
}
.numbers-stats {
    color: white;
}
.myactive > a {
    color: #6c9ae2 !important;
}
.media-heading > a {
    color: #8c9c9a;
}
.footer {
    background-color: #0e151d;
}

.tooltip.top .tooltip-inner {
    background-color: #0e151d;
}
.wishlist-item {
    background-color: #453f50;
    color: white;
}

.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: #453f50;
    color: white;
}

.dropdown-menu {
    background-color: #0e151d;
}
.dropdown-menu > li > a {
    color: white;
}

.icon {
    filter: brightness(0) invert(1);
}

#exTab1,
#exTab1 .tab-content {
    background-color: #23232d;
}

.gradient {
    background: linear-gradient(to bottom, rgb( 24, 36, 51) 0%, rgb( 14, 21, 29) 100%);
    background-color: #0e151d;
}

.pc {
    background-color: transparent !important;
}

#exTab1 .nav-pills > li > a {
    background-color: #223142;
    color: white;
}
.nav-pills > li.active > a,
.nav-pills > li.active > a:focus {
    background-color: #0e151d !important;
    color: white;
}
.comment-striped {
    background-color: #453f50 !important;
}

.input-lg.select2-container--krajee .select2-selection--single,
.input-group-lg .select2-container--krajee {
    background-color: #23232d;
}

.dropdown-list-dark {
    background-color: #23232d;
}
.messages {
    background-color: #17324c;
}
.statement {
    background-color: #182433;
}

.feed {
    color: white;
}

.list-group-item {
    background-color: #204161;
}
.message-positive .well {
    background-color: #182433;
}

.feed {
    background-color: #182433;
}

.message-cancellation .well {
    background-color: #182433;
}
}
