css:
@-moz-document url-prefix("https://lowcygier.pl/") {
body {
    color: #b3bbca;
    background-color: #272C35;
}

body.dark {
    color: #b3bbca;
}

/*
body:not(.background-image) .intro .container::before {
    content: '';
    background: rgb(31,35,42);
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    z-index: 0;
}
*/
.sidebar-widget h3 a {
    color: #e1e4ea;
}

.sidebar-widget h3 {
    border-bottom: 1px solid rgba(179, 187, 192, 0.14);
}

.sidebar-widget h3 a::after {
    border: 1px solid #b3bbc0;
    border-width: 2px 2px 0 0;
}

.search-results-header-widget h1 {
    color: #e1e4ea;
}

.main-container {
    background: #272C35;
}

.content-column .entry {
    background-color: #1F232A;
}

.pagination-widget ul .current {
    background-color: #363d49;
    border-color: #363d49;
    color: #b3bbca;
}

.pagination-widget ul a:hover {
    background-color: #363d49;
    border-color: #363d49;
}

.pagination-widget ul a {
    border: 1px solid #363d49;
}

.pagination-widget ul a,
.pagination-widget ul span {
    color: #b3bbca;
    background: #1f232a;
}

.footer-widget {
    background: #1f232a;
}

/* NEWS */
.post .nc-main .post-title a {
    color: #e1e4ea;
}



.post .nc-meta,
.post .nc-submeta {
    color: #b3bbca80;
}

.post-widget .nc-meta a {
    background: #272c35;
}

.post-widget .nc-meta a:hover {
    color: inherit;
    background: #363d49;
}

.sidebar .entry {
    border-bottom: 1px solid rgba(179, 187, 192, 0.14);
}

.discount-code {
    background: #272c35;
    border: 1px dashed #576175;
}

.discount-code:hover {
    background: #363d49;
}


.socials-share-widget .direct-link {
    border-bottom: 1px solid rgba(179, 187, 192, 0.14);
}

.socials-share-widget .btn-icon.twitter .svg-icon,
.socials-share-widget .btn-icon.facebook .svg-icon {
    fill: #e1e4ea;
}


.border-top {
    border-top: 1px solid rgba(179, 187, 192, 0.14)!important;
}


.ui-widget-header {
    border: 1px solid #363d49;
    background: #272C35;
    color: #b3bbca;
    font-weight: 700;
}

.ui-widget-content {
    border: 1px solid #363d49;
    background: #272C35;
    color: #b3bbca;
}

.ui-state-default,
.ui-widget-content .ui-state-default,
.ui-widget-header .ui-state-default {
    border: 1px solid #363d49;
    background: #23272f;
}

/* FILTRY */
.search-widget {
    background-color: #414a58;
}

.filters-widget .filtrowanie .btn {
    background-color: #363d49;
    border-color: #414a58;
}

.filters-widget .filtrowanie .btn:active,
.filters-widget .filtrowanie .btn:hover {
    background-color: #414a58;
}

.filters-widget .filtrowanie .filters-group.show .btn {
    background-color: #414a58;
}

.dropdown-menu {
    background-color: #414a58;
}

/* ADBLOCK */

.alert.alert-post {
    background: #1f232a;
}

.alert.alert-grey {
    background: #d9534f;
        color: #272c35;
}
}
