css:
@-moz-document domain("magic.wizards.com") {
/*Main color variables*/
:root {
    --main-background: #181818;
    --darker-background: #111111;
    --lighter-background: #212121;
    --main-text: #bcbcbc;
    --brighter-text: #e5e5e5;
    --orange-highlight: #be630e;
    --lighter-highlight: #e0b690;
}

#mobile-menu .header, header, #menu .top_level .mega_menu{
    background-color: var(--darker-background);
}

h1 span {
     color: var(--brighter-text);
}

h2 span {
    background: none;
    background-color: var(--darker-background);
    color: var(--brighter-text);
}

body, p, .module_card--details, .module_featured-story-header__intro-content p, .module_story-archive__grid-item p, .module_featured-stories-databank .module_featured-stories-databank__content .module_featured-stories-databank__stories-grid .module_featured-stories-databank__row-container .module_featured-stories-databank__row .module_featured-stories-databank__story span, .module_featured-stories-databank .module_featured-stories-databank__content .module_featured-stories-databank__menu ul li .module_featured-stories-databank__item, ul {
    color: var(--main-text);
}

#content-wrapper {
    background: var(--main-background);
}

a:hover, a:focus {
    color: var(--lighter-highlight);
}

.articles-bloc .article .wrap {
    border: 3px solid var(--main-background);
}

.articles-bloc .article .wrap::before {
    background: #000;
}


.articles-bloc .article .details .date {
    color: var(--brighter-text);
}

.module_featured-header--content h2 {
    text-shadow: none;
}
.module_featured-header--content--articles--article--link p.module_featured-header--content--articles--article--link--date {
    color: var(--brighter-text) !important;
}

.module_featured-header--content--actions a:hover, .module_featured-header--content--actions a:focus, .module_featured-header--content--actions button:hover, .module_featured-header--content--actions button:focus, .module_featured-header--content--articles--article--quick-links > div > a > div:hover {
    color: var(--orange-highlight);
    background: var(--lighter-background);
    transition: color .25s ease;
}

.articles-bloc .article .details .title, .articles-bloc .article .details .auth {
    color: var(--brighter-text);
}

.articles-bloc .article .details {
    background-image: linear-gradient(to top, 
#000 0%, rgba(0, 0, 0, 0.8) 30%, rgba(0, 0, 0, 0) 100%);
}

.module_product-featured--information span:not(:first-child)::before {
    background: var(--main-text);
}
.module_product-featured--information a, .module_featured-events--carousel--item a .module_featured-events--carousel--item--text h3 {
    color: var(--brighter-text);
}

.module_featured-events {
    background-color: var(--lighter-background);
}

.module_featured-header--background {
    background-color: var(--main-background);
    background-image: none !important;
}

.module_featured-header--content--articles--article--link, .module_featured-header--content--articles--article--link, .module_featured-header--content--articles--article--quick-links > div > a > div {
    background-color: var(--lighter-background);
    color: var(--brighter-text);
}
.module_featured-header--content--articles--article--link p.module_featured-header--content--articles--article--link--text {
    color: var(--main-text);
}
.module_featured-header--content h1 {
    height: 50px;
}

.daily-mtg-animated-logo.loaded{
    display: none;
}

/* story */
.module_featured-story-header__intro {
    background: none;
}

.module_featured-story-header__intro-image::before {
    background: linear-gradient(to right, var(--darker-background), rgba(236,236,236,0));
}
.module_featured-story-header__intro-image::after {
    background: linear-gradient(to bottom, rgba(0, 0, 0, 0), var(--darker-background));
}

.module_featured-story-header__tabs-content-item .module_story-content-element {
    background: var(--main-background)
}

.module_footer, .module_footer .module_footer--utils, .module_footer .module_footer--copyrights {
    background: var(--darker-background)
}
}
