css:
@-moz-document url-prefix("https://smotret-anime.com"), url-prefix("https://anime365.ru"), url-prefix("https://smotret-anime.online") {
::selection {
    color: #e1d8d8;
    background: #c4c2c2;
}
.full-background {
    /* Настройки фона */
    background: #191919 !important;
}
html {
    color: #bbb !important;
}
a {
    color: #999;
}
a:hover {
    color: #777 !important;
}
.nav-wrapper.container {
    background-color: #191919;
}
.white {
    background-color: #191919 !important;
}
nav ul a:hover,
nav ul li.active {
    background-color: #252525;
}
.dropdown-content {
    background-color: #383838
}
.green-text.text-darken-4 {
    color: #cacaca !important;
}
.card .card-image {
    -webkit-filter: opacity(80%);
    filter: opacity(80%);
}
.brand-logo {
    background: url(https://sun9-54.userapi.com/c858020/v858020519/f9066/ZZp85gfmW_M.jpg) no-repeat !important;
    width: 170px !important;
    background-size: 100% !important;
    background-position-x: center !important;
    background-position-y: 6px !important;
}
.button-collapse,
nav ul.right a {
    color: #bbb !important;
}
nav .dropdown-content li.active,
nav .dropdown-content li:hover,
nav ul li.active,
nav ul li:hover {
    background: #444;
}
.card,
.card-panel {
    background-color: #222;
    color: #bbb;
}
.green.darken-4 {
    background-color: #333 !important;
    color: #bbb !important;
}
.m-new-episodes {
    background-color: #222;
}
.collection.with-header .collection-header {
    background-color: #222;
    border-bottom-color: #888;
}
.collection .collection-item {
    background-color: #222 !important;
}
.pagination li a {
    color: #888;
}
.m-block-title a.btn {
    background-color: #333;
    color: #bbb;
}
.orange-text.text-darken-4 {
    color: #acacac !important;
}
.tag {
    background-color: #333;
    color: #bbb;
}
.light-blue.darken-4 {
    background-color: #555 !important;
    color: #d2d0d0;
}
.green-text {
    color: #818181 !important;
}
.orange {
    background-color: #191919 !important;
}
.orange.accent-4 {
    background-color: #444 !important;
}
.btn.orange.accent-4:hover {
    background-color: #333 !important;
}
.white-text {
    color: #bbb !important;
}
.m-footer-genres a {
    color: #bbb;
}
.m-moments-list-card.slick-slider .slick-dots li.slick-active button:before {
    color: #575757;
}
.btn-floating,
.btn-floating:hover,
.side-nav a.btn-floating:hover {
    background-color: #444;
}
.m-moment__play-button.material-icons {
    color: #bbb;
}
.m-alert-success {
    background-color: #555;
}
.red {
    background-color: #323232 !important;
}
.m-alert a,
.m-alert-error a,
.m-alert-notice a,
.m-alert-success a {
    color: #e3dcdc !important;
}
.m-alert,
.m-alert-error,
.m-alert-notice,
.m-alert-success {
    color: #bbb;
}
.purple-text.text-darken-4 {
    color: #818181 !important;
}
.vjs-default-skin .vjs-big-play-button {
    background-color: #333 !important;
    border-color: #333 !important;
}
.m-select-translation-type .btn.active {
    background-color: #454545 !important;
}
.btn.green.darken-4:hover {
    background-color: #777 !important;
}
.m-select-translation-variant .current {
    color: #999;
}
.m-comment.m-comment-new.collection-item.avatar .circle {
    margin-top: 35px;
}
.card .card-content p,
.m-comments.collection .collection-item.avatar p,
.m-comments.collection p {
    margin-bottom: 30px;
}
.m-comment-author-name {
    color: #f6f6f7;
}
.m-comment-meta {
    margin-bottom: 30px;
}
.input-field textarea.materialize-textarea {
    background-color: #282828;
    padding-left: 15px;
}
.m-embed-code-link {
    border-bottom-color: #00ff29 !important;
}
.ccs-wrap {
    background-color: #282828;
}
input[type=search] {
    background-color: #282828;
    padding-left: 5px;
}
.ccs-sugges .dropdown-content li {
    background-color: #333;
    color: #bbb;
}
.ccs-sugges .dropdown-content li:hover {
    background-color: #444;
}
.ccs-chip-field {
    background-color: #444 !important;
    color: #cecdcd;
}
.ccs-chip.chip {
    background-color: #555 !important;
    color: #cecdcd;
}
.ccs-sugg .btn.green {
    background-color: #888 !important;
}
.ccs-sugg .btn.red {
    background-color: #666 !important;
}
.ccs-sugg .btn.green.checked {
    background-color: #999 !important;
}
.ccs-sugg .btn.red.checked {
    background-color: #999 !important;
}
.dropdown-content li > a,
.dropdown-content li > span {
    color: #bbb;
}
.select-wrapper input.select-dropdown {
    background-color: #303030;
    padding-left: 15px;
    border-top: 5px;
}
.dropdown-content li.active.selected {
    background-color: #444 !important;
}
.dropdown-content li.active,
.dropdown-content li.selected,
.dropdown-content li:hover {
    background-color: #555;
}
.file-field,
.range-field,
.select-wrapper {
    padding-top: 5px;
}
table.striped > tbody > tr:nth-child(odd) {
    background-color: #222;
}
.m-custom-tabs {
    background-color: #222 !important;
}
.m-custom-tabs__tab a {
    color: #bbb;
}
.m-animelist-item:hover {
    background-color: #333 !important;
}
#onesignal-bell-container.onesignal-reset .onesignal-bell-launcher.onesignal-bell-launcher-theme-default .onesignal-bell-launcher-button svg .background {
    fill: #333 !important;
}
.m-alert-notice {
    background-color: #444;
}
.m-pop-box-both {
    background-color: #282828;
}
#cf-wrapper #cf-error-details {
    background: #191919 !important;
    color: #bbb;
}
#cf-wrapper #cf-error-details .cf-highlight {
    background: #222 !important;
}
#cf-wrapper h1,
#cf-wrapper h2,
#cf-wrapper h3 {
    color: #b7b7b7 !important;
}
#cf-wrapper h4,
#cf-wrapper h5,
#cf-wrapper h6,
#cf-wrapper p {
    color: #bbb;
}
#onesignal-popover-container #onesignal-popover-dialog {
    background-color: #333 !important;
}
#onesignal-popover-container #onesignal-popover-dialog .popover-button.secondary {
    background-color: #444 !important;
    color: #bbb !important;
}
#onesignal-popover-container #onesignal-popover-dialog .popover-button.primary {
    background-color: #555!important;
    color: #bbb !important;
}
}
