css:
@-moz-document url-prefix("https://wrc.net.pl/") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #E30613 #1c1c1c !important;
}
.article.afterbg.aftergradient,.top-entries,#header .header-wrapper,.afterbg.afterlight::after,#footer,.wall-bg .wall-bg-inner,.gradient-lightbg{
    background: #1c1c1c;
}
#header .bottomnav,.scrollnav.show-nav{
    background: #E30613;
}
.text-content.single-page-content.margin-bottom30 p,#header .bottomnav ul li a,.wp-pagenavi .current,.wp-pagenavi .nextpostslink,.wp-pagenavi .previouspostslink,.person h3 a,#footer ul li a,.scrollnav ul li a:hover,.text-content p:first-of-type,.text-content.single-page-content p,.text-content.text-continue p:first-of-type,.detal-autor,.detal-autor span,fieldset p,#zamowienie p,.text-content.text-continue p{
    color: #fff;
}
.text-content.single-page-content.margin-bottom30 p a{
    color: #3f6df4;
}
.section-title{
    color: #fff;
}
.fab,.tags a,.tags::before{
    color: #fff;
}
.socials a{
    border: .0625rem solid #fff;
}
.article-title.darkblue a,.article-meta a,.article-meta .times{
    color: #fff;
}
.sidebar,.sticky.is-stuck{
    background: transparent;
}
#footer .logo img{
    background: #fff;
}
.text-content h1, .text-content h2, .text-content h3, .text-content h4, .text-content h5, .text-content h6,.text-content a.link-style{
    color: #E30613;
}
#header .bottomnav ul li a:hover{
    color: #08142E;
}
.text-content .wp-caption .wp-caption-text{
    background: #E30613;
    color: #fff;
}
.afterbg{
    scrollbar-color: #E30613 #1c1c1c;
}
.avatar-autor{
    border-color: #E30613;
}
.input{
    background: #1c1c1c;
    border-color: #E30613;
    color: #fff;
}
#menu li{
    background: transparent;
    
}
#menu li a{
    color: #fff;
}
#menu li a:hover{
    color: #08142E;
}
#footer ul:after{
    content: " | Ciemny motyw by Maciejozaman";
    color: #E30613;
}
}
