css:
@-moz-document regexp('(https:\/\/cms\.mmu\.edu\.my).*')
{
a.EOPP_SCCHILDCONTENTLINK, .EOPP_SCCHILDCONTENTLINK, .EOPP_SCCHILDFOLDERLINK, .EOPP_SCMORELINK, .EOPP_SCCHILDCONTENTLINK, .EOPP_SCCHILDFOLDERLINK, .EOPP_SCMORELINK {
   	color: #ffffff;
 	font-family: 'Montserrat', sans-serif;
    font-size: 11px;
    line-height: 0;
    font-weight: 600;
    text-decoration: none;
    background: #3d3d3d;
    padding: 19px 21px;
    box-shadow: 4px 4px 6px rgba(0,0,0,0.26);
    display: inline-flex;
    border-radius: 4px;
    margin-bottom: 10px;
}



.EOPP_SCSECTIONCONTENTLINK, .EOPP_SCSECTIONFOLDERLINK, a.EOPP_SCSECTIONCONTENTLINK {
 font-family: 'Montserrat', sans-serif;
    font-weight: 600;
    display: inline-flex;
    text-decoration: none;
    background: #105687;
  	padding: 7px 14px;
    font-size: 14px;
    letter-spacing: 1px;
    border-radius: 4px;
    color: white;
    display: block;
}
a.EOPP_SCSECTIONCONTENTLINK {
    padding: 14px 28px;
}
.EOPP_SCSECTIONFOLDERLINK{
    display: block !important;
}
.EOPP_SCADDITIONALTEXT {
   font-family: 'Montserrat', sans-serif;
    font-size: 12px;
    font-weight: 600;
    font-style: normal;
    color: rgb(51,51,51);
    margin: -4px 45px 19px 26px;
    display: block;
}

.EOPP_SCSECTIONCONTENT, .EOPP_SCSECTIONFOLDER{
    background-color: rgb(255, 255, 255);
}

.EOPP_SCPAGEDESCRSECTION {
    display: none;
}
.EOPP_SCPAGETITLESECTION{
    display: none;
}
.EOPP_SCBREADCRUMBSECTION{
    display: none;
}
a.EOPP_SCSECTIONLEFT {
    display: none;
}
li {
    background: none !important;
}
ul{
    display: flex;
    max-width: 30vw;
    flex-wrap: wrap;

}
#pthdr2container {
        height: 54px;
    background: #333333;
    margin: 0px 0 20px 0;
}
dl#pthdr2links dt a {
      color: #FFFFFF;
  	font-family: 'Montserrat', sans-serif;
    font-size: 13px;
    font-weight: 500;
    vertical-align: middle;
    text-decoration: none;
    margin: 0 3em;
    position: relative;
    top: -15px;
    left: -36vw;
}
#pthdr2logoswan{
    background: none;
}
#pthnavcontainer {
    display: none !important;
}
.EOPP_SCSECTIONCONTENT, .EOPP_SCSECTIONFOLDER {
    background-color: rgb(255, 255, 255);
    border: none;
}
}
