css:
@-moz-document regexp('(https:\/\/cms\.mmu\.edu\.my).*'){
.SSSWEEKLYA1BACKGROUND {
   	font-family: 'Montserrat', sans-serif;
    font-size: 10px;
    font-weight: bold;
    color: #005694;
    background-color: rgb(255, 255, 255);
    text-align: inherit;
    vertical-align: middle;
    border-color: rgb(193,201,219);
    border-style: none;
    border-radius: 0px;
    min-width: 150px;
    padding: 8px 0;
    line-height: 17px;
    border: 1px solid #005b9c;
    border-width: 0px 0px 0px 4px;
    padding: 0 0 0 15px;
    height: 58px;
}

.PSLEVEL3GRIDCOLUMNHDR, .SSSWEEKLYDAYBACKGROUND {
   	font-family: 'Montserrat', sans-serif;
    font-size: 10px;
    font-weight: bold;
    color: #005694;
    background-color: rgb(255, 255, 255);
    text-align: inherit;
    vertical-align: middle;
    border-color: rgb(193,201,219);
    border-style: none;
    border-radius: 0px;
    min-width: 150px;
    padding: 8px 0;
    line-height: 17px;
    border: 1px solid #005b9c;
    border-width: 0px 0px 0px 4px;
    padding: 0 0 0 15px;
    height: 58px;
}

.SSSWEEKLYBACKGROUND{
 	background: #fff6e5;
  	border:1px solid #aa7100;
    border-radius: 0;
    padding: 15px 0;
    border-width: 0 0 0 4px;
}

.PSLEVEL3GRID {
     border-color: rgb(244, 244, 244);
}

.SSSWEEKLYTIMEBACKGROUND {
    color: #005b9c;
    background-color: rgb(255, 255, 255);
    border: 1px solid #005b9c;
  	font-family: 'Montserrat', sans-serif;
    border-width: 0 0 0 4px;
    height: 60px;
}

.SSSTEXTWEEKLYTIME {
  font-size: 11px;
    padding: 0px 20px;
    font-weight: bold;
    color: #005b9c;
    background-color: #ffffff;
    border-radius: 13px;
    text-align: inherit;
}

.SSSTEXTWEEKLY {
	font-family: 'Montserrat', sans-serif;
    font-size: 11px;
    font-weight: 700;
    font-style: normal;
    color: rgb(131, 69, 0);
    text-align: initial;
    padding: 0 23px 0 15px;
    line-height: 18px;
}

.PSCHECKBOX, .PSLONGEDITLABEL {
  	font-family: 'Montserrat', sans-serif;
    font-weight: 500;
    font-style: normal;
    color: white;
    font-size: 11px;
    line-height: 34px;
    background: #0057b0;
    padding: 9px 24px;
    border-radius: 5px;
    margin: 13px 11px;
}

.SSSBUTTON_BLUELINK{
  	font-family: 'Montserrat', sans-serif;
    font-weight: 600;
    font-style: unset;
    color: #581c00;
    font-size: 13px;
    line-height: 34px;
    background: #f2a700;
    padding: 7px 24px;
    border-radius: 5px;
    box-shadow: none;
    margin: 13px 11px;
    display: table-cell;
    border: none;
    text-transform: capitalize;
}

.PSGROUPBOXLABEL {
	font-family: 'Montserrat', sans-serif;
    font-size: 12px;
    font-weight: 600;
    font-style: normal;
    color: rgb(255,255,255);
    background: #313131;
    border: none;
    padding: 12px 0 12px 20px;
    border-radius: 4px;
    box-shadow: 3px 3px 6px rgba(0,0,0,.20);
}
.SSSBUTTON_BLUELINKHOVER, .SSSBUTTON_BLUELINK:HOVER{
    display: table-cell;
    background: none;
    border: none;
}
}
