css:
@-moz-document url-prefix("https://sms12.schoolsoft.se/") {
.login_mainframe, .login_mainframe_extended, body, body.login, .login_formtable {
    background: #333333;
}
.h3, .rubrik, .calendarheader, .form-groupheader, .formheader {
    color: #d8d8d8;
    font-size: 16px;
    text-align: center;
}
.content_wrapper {
    padding: 9px;
    width: 75vw;
    float: left;
    height: auto;
}
select, .select1, .select2, .select3 {
    background: #333333;
    color: #d8d8d8;
}
div.formtable, .formtable {
    background: none;
    color: #d8d8d8;
    text-shadow: unset;
}
.login_formtable {
    color: #d8d8d8;
}
.login_formtable a {
    color: #d8d8d8;
}
.img-rounded, .logo_border {
    display: none;
}
.login_formtable .login_la {
    color: #d8d8d8;
}
form {
    width: 100%;
}
.table th, .table td, .table_no_hover th, .table_no_hover td {
    border-top: unset;
}
table {
    border-collapse: unset;
}
.xbackground {
    background: none;
}
#header {
    background: none;
}
#top {
    background: #292929;
    display: flex;
    justify-content: space-around;
}
#top_left {
    left: 10px;
    top: 45px;
    background: none;
}
#logo {
    background: none;
}
#top_content {
    padding: 0;
}
#top_content_wrapper #menu {
    background: #292929;
}
#top_content_wrapper {
    width: 100%;
    top: unset;
    padding: 0;
    position: unset;
}
.TopMenu-Thick-Left, .TopMenu-Thick-Bg-Left, .TopMenu-Menu-Left-Bg, .TopMenu-Thick-Circle, .TopMenu-Menu-Right-Bg, .TopMenu-Thick-Bg-Right, .TopMenu-Thick-Bg-Right-2, .TopMenu-Thick-Right, .TopMenu-MenuActive-Left, .TopMenu-MenuInactive-Bg, .TopMenu-MenuActive-Right, .TopMenu-MenuActive-Bg {
    background: none;
}
#menu a {
    color: #d8d8d8;
}
.TopMenu-MenuActive-Bg a:hover, .TopMenu-MenuInactive-Bg a:hover {
    border-bottom: 1px solid #d8d8d8;
}
.TopMenu-MenuActive-Bg a {
    color: white !important;
}
#main {
    background: #292929;
}
#top_content_wrapper {
    left: unset;
}
#container, #header {
    display: flex;
    justify-content: center;
    width: 100%;
}
.leftmenu .colright {
    float: left;
    position: relative;
    display: flex;
    width: 85%;
    left: 1px;
    justify-content: center;
}
.menu_top, .menu_header, .menu_item {
    color: #d8d8d8;
}
.menu_item:hover {
    background: #333333;
    color: #d8d8d8;
}
.h2_box {
    background: none;
    border: none;
}
.h1 {
    color: #d8d8d8;
}
.btn {
    background: none;
    text-shadow: none;
    color: #d8d8d8;
}
.btn:hover {
    background: none;
    color: #e8e8e8;
}
.h2, .h3 {
    color: #d8d8d8;
    text-shadow: unset;
}
.h2_inner, .h2_innerno_pad {
    background: none;
}
.table-striped tbody tr:nth-child(odd) td, .table-striped tbody tr:nth-child(odd) th {
    background: none;
    color: #d8d8d8;
}
.h3_bold {
    color: #eae8e8;
}
.h2_inner, .h2_innerno_pad {
    border: 2px solid #312f2f;
}
.table a {
    color: #d8d8d8;
}
.leftmenu .col2 {
    position: absolute;
    right: 10px;
    left: 80vw;
    top: unset;
}
.nav > li > a {
    text-decoration: none;
    color: #d8d8d8;
}
.nav > li > a:hover {
    background: none;
    color: #e8e8e8;
}
#leftMenu {
    background: #292929;
}
.nav-tabs > .active > a, .nav-tabs > .active > a:hover {
    color: #d8d8d8;
    background-image: none;
    border-color: none;
    background: none;
    border: none;
}
}
