css:
@-moz-document domain("youtube.com") {
/* Addons  styling for compatibility */
 #info > #count > a > span {
    color: rgb(77, 87, 83) !important;
}
/* Darker "Simple YouTube to MP3/MP4 Converter" */

/* /Addons */
 #logo > #logo-icon, #endpoint > *:nth-child(2) > *:not(:first-child), #appbar-guide-button > span > span, .privacy-icon-clickable, .private-icon, .public-icon, #trigger > #label > #label-icon > svg > g, .yt-masthead-picker-info > a:nth-child(4) > span, #yt-masthead-user > a, #yt-masthead-notifications-button {
    background-color: transparent !important;
}
/* subscribe button text*/
 yt-formatted-string#text {
    color: white !important;
}
.toggle-bar.paper-toggle-button {
    background-color: var(--paper-toggle-button-unchecked-bar-color, #fff);
}
html:not([style-scope]):not(.style-scope) {
    --ytd-toggle-color: #be2a1b;
    --ytd-survey-button-color: rgb(190, 42, 27);
}
html[dark]:not([style-scope]):not(.style-scope), [dark]:not([style-scope]):not(.style-scope) {
    --ytd-toggle-color: hsl(3, 81.8%, 49.6%);
    --ytd-survey-button-color: rgb(190, 42, 27);
}
.yt-uix-button-subscribe-unbranded {
    background-color:rgba(169, 25, 25, 0.78) !important;
}
#player-ads, #google_companion_ad_div, #expand-ad, #google_image_div, .ytd-player-legacy-desktop-watch-ads-renderer, .adDisplay {
    visibility: hidden !important;
    height: 0px !important;
    width: 0px !important;
    font-size: 0px !important;
    display: none !important;
}
#notification-button > * {
    filter: brightness(1.25) !important;
    border-radius: 50% !important;
}
/*notifications about channel - subscribed to */
 .guide-icon > svg > g:nth-child(1), .paper-tab:hover, [aria-label="Unlike"] {
    filter: sepia(100%) hue-rotate(156deg) saturate(700%) brightness(.75) !important;
    transform: scale(1.14) !important;
}
#label > yt-formatted-string {
    color: white !important;
    transition: color .5s ease-out !important;
}
/* Filter choices */
 #label > yt-formatted-string:hover {
    color: rgb(150, 180, 230) !important;
    transition: color .3s ease-in !important;
    cursor: pointer !important;
}
/* Filter choices */
 #movie_player > .ytp-gradient-bottom, .ytp-chrome-top, .ytp-gradient-top {
    display: none !important;
}
[aria-label="Play"] > ytp-play-button ytp-button:hover > svg > path {
    fill: rgb(190, 42, 27) !important;
}
[aria-label="Pause"] > ytp-play-button ytp-button:hover > svg > path {
    fill: rgb(190, 42, 27) !important;
}
/* Play button */
 .ytp-play-controls > ytp-play-button:hover > svg > path {
    fill: rgb(190, 42, 27) !important;
}
/* Previous/replay button */
 .ytp-left-controls > .ytp-next-button:hover > svg > path {
    fill: rgb(190, 42, 27) !important;
}
/* Previous/replay button */
 .ytp-mute-button:hover > svg > path {
    fill: rgb(190, 42, 27) !important;
}
/* Mute/unmute button */
 .ytp-volume-slider-handle:hover:before, .ytp-volume-slider-handle:hover {
    background: rgb(190, 42, 27) !important;
    z-index: 9999 !important;
}
/* Volume slider */
 .ytp-right-controls > button:hover > svg > path, .ytp-fullscreen-button:hover > svg > g > path {
    fill: rgb(190, 42, 27) !important;
}
/* Settings/scale/fullscreen buttons */
 .ytp-settings-button.ytp-hd-quality-badge::after {
    background-color: rgb(190, 42, 27) !important;
}
#container {
    border-bottom: 1px solid rgba(11, 11, 12, 1);
}
.title.ytd-guide-entry-renderer {
    font-weight: 900;
}
.ytp-red2 .ytp-swatch-background-color, .ytp-red2 .ytp-swatch-background-color-secondary {
    background-color: rgb(39, 147, 230) !important;
}
/* Progress Bar */

/* live chat */
 yt-live-chat-message-buy-flow-renderer {
    background-color: rgb(48, 80, 93) !important;
}
#purchase-amount, .yt-live-chat-text-input-field-renderer {
    color: black !important;
}
#picker-buttons {
    background-color: rgb(78, 110, 123) !important;
}
#notification-count.ytd-notification-topbar-button-renderer {
    color: rgb(110, 170, 205) !important;
    transform: scale(1.2) !important;
}
/* notification number */
 .view-stats-metric-link:hover, [title="View analytics"]:hover, [data-content-id="yt-uix-tooltip14-content"]:hover {
    background-color: rgb(39, 44, 50) !important;
}
.unseen {
    filter: brightness(1.2) !important;
}
/* unseen notification */
 #play-all > ytd-button-renderer > a:hover {
    filter: brightness(1.2) !important;
}
#creator-page-content > div, #non-appbar-vm-video-actions-bar, #vm-playlist-video-list-ol > li > div, [aria-label="Action menu."], .vm-addto-button, .edit-expand-menu-button, .vm-playlist-edit-video, #creator-sidebar > li > h3 > a:hover, #creator-page-content > div:nth-child(10) > div {
    background-color: rgb(41, 40, 40) !important;
}
.account-section-setting > span > span, .yt-uix-form-input-select-content, #footer-main > ul > li > button, #footer-main > a, #footer-main > button, #footer-main >[data-loaded="1"], .creator-sidebar-item-link:hover, .sort-menu > div > button, #creator-page-content *:not(.ytrp_wrapper):not(.ytrp_tooltip):not(.ytrp_rb_bg_bottom):not(.ytrp_rb_bg):not(.C2KYWWB-g-e), #creator-sidebar-section-id-dashboard > h3 > a, #creator-page-content {
    background-color: rgb(37, 37, 38) !important;
    border-color: grey !important;
}
.creator-sidebar-item.selected {
    background-color: rgb(0, 50, 70) !important;
}
.yt-uix-button-subscribe-unbranded {
    background-color: rgb(37, 37, 38) !important;
    border-color: grey !important;
}
/* Subscribers unsubscribed to */
 .yt-uix-button-subscribed-unbranded {
    background-color: rgb(28, 27, 27) !important;
    border-color: grey !important;
}
/* Subscribers subscribed to  */
 .send-button-info > button {
    filter: invert(89%) !important;
}
#search-form > * {
    border: none !important;
    box-shadow: .45px .45px .45px .45px rgba(110, 120, 130, .35) !important;
}
.C2KYWWB-b-k *:not(.C2KYWWB-n-c), #gwt-debug-datePicker *:not(.C2KYWWB-K-c), .C2KYWWB-X-d * {
    background: transparent !important;
}
iron-dropdown > #contentWrapper > ytd-menu-popup-renderer > paper-menu *:not(yt-formatted-string):hover, .yt-ui-menu-content > button:hover, .yt-ui-menu-content > .addto-playlist-button:hover, #addto-list-panel > div > ul > li:hover, [title="Enter a playlist name to search for"] {
    background-color: rgb(70, 70, 80) !important;
    color: rgb(0, 0, 0) !important;
}
yt-formatted-string, #buttons * {
    background-color: transparent !important;
}
#search-icon-legacy.ytd-searchbox:hover {
    filter: sepia(100%) brightness(1) hue-rotate(180deg) !important;
}
paper-tooltip * {
    background: rgba(10, 11, 12, .75) !important;
    border-radius: 55px / 25px !important;
    color: azure !important;
}
#chat {
    height: 30% !important;
}
yt-live-chat-text-message-renderer > #menu {
    filter: invert(70%) brightness(1) !important;
}
#author-name, .yt-uix-form-input-text {
    color: rgb(85, 95, 115) !important;
}
#expander > #content > #content-text {
    color: rgb(190, 192, 192) !important;
}
#show-hide-button {
    border-bottom: 1px white solid !important;
}
@keyframes subSub {
    from {
        border-radius: 400px 70px/90px 100px 0px
    }
    to {
        border-radius: 300px 130px/100px 400px 150px
    }
}
@keyframes notNot {
    from {
        border-radius: 100px
    }
    to {
        border-radius: 15px
    }
}
paper-button.ytd-subscribe-button-renderer {
    background-color: rgba(45, 46, 53, .5) !important;
    color: rgb(100, 100, 100) !important;
    border-radius: 400px 70px/90px 100px 0px;
    transition: background-color .5s ease-out !important;
}
paper-button.ytd-subscribe-button-renderer:hover {
    background-color: rgb(70, 80, 100) !important;
    color: white !important;
    animation-duration: .6s;
    animation-name: subSub;
    animation-iteration-count: infinite;
    animation-direction: alternate;
}
#notification-button:hover, .ytp-sb-unsubscribe {
    background-color: rgb(30, 40, 80) !important;
    animation-duration: .4s;
    animation-name: notNot;
    animation-iteration-count: infinite;
    animation-direction: alternate;
}
#masthead-container > #masthead, #yt-masthead-container, #channel-header, #tabs, ytd-browse[page-subtype="channels"] {
    background: rgb(20, 22, 25) !important;
}
#guide-wrapper > #guide-content *, #tabs-container > #tabs-inner-container {
    background: rgb(0, 0, 0) !important;
}
/* Notifications */
[aria-label="Notifications"] > yt-icon > svg > g > path {
    fill: rgb(255, 255, 255) !important;
    color: rgb(0, 255, 255) !important;
}
/* /Notifications */
 ytd-multi-page-menu-renderer > #container > #sections {
    overflow: hidden !important;
}
#left-arrow-container, #right-arrow-container, #selectionBar.paper-tabs {
    background: rgba(120, 110, 110, .8) !important;
    filter: invert(100%) !important;
}
#search-icon-legacy {
    border: 1px rgba(50, 100, 100, .0) solid !important;
    border-radius: 0px 10px 10px 0px !important;
}
ytd-author-comment-badge-renderer {
    background-color: rgb(46, 83, 89) !important;
}
#author-comment-badge > ytd-author-comment-badge-renderer > a > yt-formatted-string {
    color: rgb(116, 133, 139) !important;
}
#author-comment-badge > ytd-author-comment-badge-renderer > a > yt-formatted-string:hover {
    color: rgb(146, 163, 169) !important;
}
ytd-comment-renderer > #body > #author-thumbnail > a > yt-img-shadow {
    transform: scale(1.3) !important;
    filter: brightness(.95) !important;
}
*:not(#search-icon-legacy):not(.ytd-searchbox):not(#search):not(#ytd-searchbox):not(#search-container):not(#container) {
    border-bottom-color: #be2a1b !important;
}
#guide {
    border-right: 1px solid rgba(11, 11, 12, .3);
    transform: scale(1.1) !important;
    margin-top: 44px !important;
    margin-right: 2000px !important;
    display: table-cell;
    z-index: 99 !important;
}
#guide-icon > svg > g {
    filter: invert(100%) brightness(2) !important;
}
.ytp-swatch-background-color-secondary * {
    background-color: rgb(230, 230, 230) !important;
}
#yt-masthead-container, #masthead-appbar, #header {
    border-bottom-color: black !important;
}
#items > ytd-grid-video-renderer {
}
/* Thumbnail div */
 #dismissable > *:not([width="190"]), #dismissable > #details a {
    color:rgb(190, 13, 13) !important;
    transition: .09s !important;
}
#dismissable > #details > #meta > div > div > div > #byline > a:hover {
    color: rgb(180, 225, 255) !important;
}
#metadata-line * {
    color: rgb(150, 150, 150) !important;
}
#container:not(.ytd-masthead) {
    border-bottom: none !important;
}
/* Like/Dislike buttons */
 #top-level-buttons > ytd-toggle-button-renderer> a > button {
    transition: .1s ease-in-out !important;
}
#top-level-buttons > ytd-toggle-button-renderer:nth-child(1) > a > button:hover, #top-level-buttons > ytd-toggle-button-renderer:nth-child(2) > a > button:hover {
    filter: brightness(2.5);
    transform: scale(1.3) !important;
}
#top-level-buttons > ytd-toggle-button-renderer:nth-child(1) > a >[aria-pressed="true"] {
    filter: brightness(.8) sepia(100%) hue-rotate(150deg) saturate(2) !important;
}
#top-level-buttons > ytd-toggle-button-renderer:nth-child(2) > a >[aria-pressed="true"] {
    filter: brightness(.8) sepia(100%) hue-rotate(-40deg) saturate(5) !important;
}
[aria-label="Remove dislike"] {
    filter: sepia(100%) hue-rotate(315deg) saturate(200%) brightness(.85) !important;
}
#header {
    z-index: 1 !important;
}
#guide-inner-content * {
    background-color: rgba(30, 31, 32, .8) !important;
    background-size: 50px 50px !important;
}
#appbar-guide-menu, #appbar-guide-menu * {
    border-color: black !important;
}
.yt-formatted-string, #guide-renderer * a {
    color: #be2a1b !important;
    background: none !important;
}
.date {
    color: rgb(100, 110, 110) !important;
}
#byline {
    color: rgb(120, 150, 180) !important;
}
a:not(.yt-formatted-string) {
    color: grey !important;
}
a:hover {
    color: darkgrey !important;
}
#label-icon {
    transform: scale(1.3) !important;
}
#video-title {
    color:#be2a1b;
}
/*LOGO*/
 :root {
    --color2: #ce1404;
    --color6: #f70c0c;
    --color8: #a31f1f;
}
#logo-icon > svg > g > g:nth-child(1) > path {
    fill: var(--color2);
}
#guide-icon.ytd-masthead, .yt-icon-container.yt-icon, #label-icon.yt-dropdown-menu, #guide-icon.ytd-app, .yt-live-chat-header-renderer iron-icon {
    fill: var(--color6);
}
.yt-live-chat-icon-toggle-button-renderer iron-icon, .yt-live-chat-text-message-renderer iron-icon {
    fill: var(--color8);
}
#youtube-paths > path:nth-child(1), #youtube-paths > path:nth-child(2), #youtube-paths > path:nth-child(3), #youtube-paths > path:nth-child(4), #youtube-paths > path:nth-child(5), #youtube-paths > path:nth-child(6), #youtube-paths > path:nth-child(7) {
    fill: var(--color8)!important;
}
/*SLider*/
 .html5-scrubber-button, .ytp-scrubber-button {
    background: #be2a1b !important;
    border-color: rgba(190, 42, 27, 0.5) !important;
    box-shadow: 0 0 4px rgba(190, 42, 27,
    /* RGB */
    0.9) !important;
}
/* бегунок  при наведени*/
 .html5-scrubber-button:hover, .ytp-scrubber-button:hover {
    background: #be2a1b !important;
    border-color: rgba(190, 42, 30,
    /* RGB */
    0.5) !important;
    box-shadow: 0 0 9px rgba(190, 42, 27,
    /* RGB */
    0.9) !important;
}
/* нижний бар */
 .html5-player-chrome {
    background-color: #000 !important;
    background: linear-gradient(to bottom, #000 30%, #222 90%, #000 50%) !important;
    background: -webkit-linear-gradient(to bottom, #000 30%, #222 90%, #000 50%) !important
}
/* загрузка < */

/* загрузка > */
 .html5-play-progress, .ytp-load-progress {
    background: rgba(150, 25, 255, 0.7)!important;
    box-shadow: 0px 0px 1px rgba(150, 25, 255,
    /* RGB */
    .7)inset !important
}
/* загрузка */
 .ytp-unloaded-progress {
    background: rgba(200, 0, 160, 0.7)!important
}
/* 10 сек */
 .ytp-ad-progress {
    background: #1a7dff !important;
    background: linear-gradient(to bottom, #000 0%, #333 100%, #000 190%) !important;
    background: -webkit-linear-gradient(to bottom, #000 0%, #333 100%, #000 190%) !important
}
/* бегунок звука */
 .ytp-volume-slider-foreground {
    background: rgba(190, 42, 27,
    /* RGB */
    0.9)!important;
    box-shadow: 0px 0px 5px rgba(190, 42, 27, .9)inset !important
}
/* звук > */
 .ytp-volume-slider-foreground:after {
    background: rgba(190, 42, 27,
    /* RGB */
    0.4)!important;
    box-shadow: 0px 0px 5px rgba(190, 42, 27, .9)inset !important
}
/* звук < */
 .ytp-volume-slider-foreground:before {
    background: rgba(190, 42, 27,
    /* RGB */
    0.4)!important;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 1.0)inset !important
}
/*цвет текста*/
 .ytp-time-display, .ytp-time-current, .ytp-time-separator, .ytp-time-duration {
    color: #be2a1b!important
}
/* Video progress bar glow*/
 .html5-play-progress, .ytp-play-progress {
    background: #be2a1b;
    background: -moz-linear-gradient(top, #be2a1b 0%, #be2a1b 35%, #000 65%, #be2a1b 100%);
    background: -webkit-linear-gradient(top, #be2a1b 0%, #000 35%, #000 65%, #be2a1b 100%);
    background: linear-gradient(to bottom, #be2a1b 0%, #000 24%, #000 65%, #be2a1b 100%);
    box-shadow: 0px 0px 20px #be2a1b, 0px 0px 20px #be2a1b, 0px 0px 20px #be2a1b !important;
}
/* Video load bar */
 .html5-load-progress, .ytp-load-progress {
    background: #1a7dff;
    background: -moz-linear-gradient(top, rgba(120, 120, 120, 0.3) 0%, rgba(200, 0, 160, 0.7) 45%, rgba(0, 0, 0, 0.3) 55%, rgba(200, 0, 160, 0.7) 100%);
    background: -webkit-linear-gradient(top, rgba(200, 0, 160, 0.7) 0%, rgba(0, 0, 0, 0.3) 45%, rgba(0, 0, 0, 0.3) 55%, rgba(200, 0, 160, 0.7) 100%);
    background: linear-gradient(to bottom, rgba(200, 0, 160, 0.7) 0%, rgba(0, 0, 0, 0.3) 40%, rgba(0, 0, 0, 0.3) 55%, rgba(200, 0, 160, 0.7) 100%);
    box-shadow: 0px 0px 12px rgba(200, 0, 160, 0.7), 0px 0px 12px rgba(200, 0, 160, 0.7), 0px 0px 12px rgba(200, 0, 160, 0.7) !important;
}
}
