css:
@-moz-document domain("workservice.pl") {
body{
    background: #1c1c1c;
}
p{
    color: #fff;
}
:root{
    scrollbar-color: #FF00A2 #1c1c1c;
}
.panel,.job-offer,.offer-presentation,.offer-links,.no-answer,.bg-info{
    background: #1c1c1c;
}
.form-control{
    background: #1c1c1c;
    border-color: #FF00A2;
}
.form-control::placeholder{
    color: #fff;
}
.spectacular-offer__box .panel-body{
    background: #1c1c1c;
}
.dropdown-menu{
    background: #1c1c1c;
    color: #FF00A2;
}
.employee-tutorial .text{
    color: #fff;
}
.offer-item{
    background: #1c1c1c;
    border-color: #FF00A2;
}
.branch-list{
    scrollbar-color: #FF00A2 #1c1c1c;
}
.select2-container--bootstrap .select2-selection,.select2-dropdown{
    background: #1c1c1c;
}
.select2-container--bootstrap .select2-selection--single .select2-selection__rendered,.select2-container--bootstrap .select2-results__option,.pagination > li > a,input[type="checkbox"] + label,.form-group small,.offer-specify-text,.offer-inner-fragment,.offer-important-text,.offer-box-inner,.offer-presentation .offer-inner-fragment__left-box p,.offer-presentation .offer-inner-fragment__right-box p,.offer-presentation .offer-inner-fragment__center-box p,.offer-presentation .offer-inner-fragment__center-box p:first-child,.contact-central *{
    color: #fff;
}
.select2-container--bootstrap .select2-search--dropdown .select2-search__field{
    background: #1c1c1c;
    border-color: #FF00A2;
    color: #fff;
}
.owl-brands,.owl-partners{
    background: #1c1c1c;
}
.owl-brands.owl-carousel .owl-item{
    background: #eee;
    border-radius: 30px;
}
.nav-tabs > li.active > a{
    background: #FF00A2;
}
.nav-tabs > li.active > a:hover{
    background: #1c1c1c;
    color: #FF00A2;
}
.nav-tabs > li > a:hover{
    background: #1c1c1c;
    border-color: #FF00A2;
}
.offer-presentation .offer-content-inner{
    background: #1c1c1c;
    border-color: #FF00A2;
}
.offer-presentation .offer-inner-fragment{
    border-color: #FF00A2;
}
.offer-presentation .offer-email-box--desktop input{
    background: #1c1c1c;
    border-color: #FF00A2;
}
.offer-presentation .offer-email-box--desktop input::placeholder{
    color: #fff;
}
.contact .map-provinces{
    fill: #FF00A2;
}
.copyright .text-primary:after{
    content: " | Dark theme by Maciejozaman";
}
}
