css:
@-moz-document domain("dagbladet.no") {
/* Based on "dagbladet.no - Moerk by cfjoelstad" */
:root {
    --primaryDark: #ab0000;
    --primary: #d60000;
    --primaryLight: #ff2323;
    --primaryLighter: #ff7070;

    --darknessDark: #000;
    --darkness: #1c1d25;
    --darknessLight: #3d3f51;
    --darknessLighter: #5e617c;

    --typeDark: #fff;
    --type: #f3f3f3;
    --typeLight: #C0C0C0;
    --typeDisabled: #5e617c;

    --yellowDark: #efc327;
    --yellow: #F1CA3F;
    --yellowLight: #f6de86;
    --yellowLighter: #fbf1ce;
}

.css-pp-box.show,
.plussbox_container,
#menuwrapper,
.native-advertisement,
#pluss-banner-container {
    display: none;
}

body {
    background-color: var(--darkness) !important;
    color: var(--type);
}

/* Primary navbar */
header#main-navigation,
header#main-navigation .main-logo {
    background: var(--primaryDark);
}
header#main-navigation .main-logo a:hover,
header#main-navigation .main-logo a:focus,
header#main-navigation ul.sections li a:hover,
header#main-navigation ul.sections li a:focus {
    background: var(--primary);
}
header#main-navigation section.large-dropdown.expand {
    background: var(--darknessLighter);
}
header#main-navigation section.large-dropdown section ul li a {
    color: var(--typeLight);
}

/* News ticker */
.horizontal-scroller {
    background-color: var(--darknessLighter);
}
.horizontal-scroller .inner ul.list li a .title,
.horizontal-scroller .inner ul.list li a .text {
    color: #bfbfbf;
}
.horizontal-scroller .nav-item.start {
    background: linear-gradient(to left, rgba(255, 255, 255, 0) 0%, var(--darknessLighter) 75%);
}
body > section.secondary:after,
.horizontal-scroller .nav-item.next {
    background: linear-gradient(to right, rgba(255, 255, 255, 0) 0%, var(--darknessLighter) 75%);
}
/* Secondary navbar */
body > section.secondary {
    background-color: var(--darknessLight);
}
body > section.secondary,
body > section.secondary a {
    color: var(--type);
}
.top-nav .secondary {
    border-color: var(--darknessLight);
    background-color: var(--darkness);
}
.top-nav .secondary .small-secondary-toggle {
    background: transparent;
}
.top-nav .secondary.alternate img.logo {
    filter: invert(1);
    /* black on transparent "Mat" logo */
}

main {
    background-color: var(--darkness) !important;
}

footer#main-footer {
    background: var(--darknessLight);
}

footer#main-footer > .copyright {
    background: var(--darknessLighter);
}
footer#main-footer a.link {
    color: var(--typeLight);
}

article.preview.yellow > a,
article.preview.yellow .related {
    background: var(--yellowDark);
}

article.preview.primary > a,
article.preview.primary .related {
    background: var(--primaryDark);
}

article.preview .headline.black,
article.preview .headline .black {
    color: var(--type);
}

.ref h2 a:link,
.ref h2 a:visited {
    color: #e3e3e3 !important;
    text-decoration: none;
}

.related_items div a {
    color: var(--type) !important;
    text-decoration: none;
}

.back.gray {
    background-color: #474747 !important;
}

a:link {
    color: var(--typeLighter);
}

a:visited {
    color: var(--typeLight);
}

blockquote,
blockquote p {
    color: var(--type);
}
blockquote cite {
    color: var(--typeDisabled);
}

table tr {
    border-color: transparent;
}
table thead {
    border-top-color: var(--primaryDark);
    background-color: var(--darknessLight);
}
table tbody tr {
    background-color: var(--darknessDark);
}
table tbody tr.even,
table tbody tr.alt,
table tbody tr:nth-of-type(even) {
    background-color: var(--darkness);
}

table tr th,
table tr td {
    border-right-color: var(--darknessLight);
    border-bottom-color: var(--darknessLight);
    border-left-color: var(--darknessLight);
}

table thead,
table tbody tr th,
table tbody tr td {
    color: var(--type);
}

p.image-caption b,
p.image-caption strong {
    color: var(--typeDark);
}

p.image-caption {
    color: #B8B8B8;
}

.article-body h2 {
    color: var(--typeDark);
}

.label.black {
    background-color: var(--darknessDark);
    color: var(--type);
}

.text-red, .text-primary {
    color: var(--primaryDark) !important;
}

article.compact aside.related ul.related-list li a {
    color: var(--type);
}

.panel {
    background: var(--darknessDark);
    color: var(--type);
    border: 0;
}
.panel .section-title span {
    background: var(--darknessLight);
}
.section-title.primary:after {
    background-color: var(--primaryDark);
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: var(--typeDark) !important;
}

.article-byline .article-byline-name {
    color: var(--typeDark);
}

strong {
    color: var(--typeDark);
}

.has-fullscreen > .row {
    background-color: var(--darkness);
}
.has-fullscreen article {
    background: var(--darkness);
    z-index: 100;
}

#adunit-wallpaper-left:before,
#adunit-wallpaper-right:before,
.fullbleed .adunit-wrapper:before,
.adunit.large-fullscreen-x:before,
.adunit.large-fullscreen-x:after {
    background-color: var(--darkness);
}
.adunit:empty {
    background-color: var(--darknessDark);
}
.adunit:before,
article.preview.extrinsic > a {
    background-color: var(--darknessLight);
    color: var(--type);
}
#ad_coupon_links .button {
    background-color: var(--darknessLight);
}
#ad_coupon_links a {
    color: var(--type);
}

/* Agenda */
[class^="LinkBarNav-"]:hover {
    background: var(--primaryDark);
}
[class*="LinkBarElementBase-"]:hover {
    background: var(--primary);
}
[class^="VerticalFlexingList-"],
[class^="VerticalFlexingList-"]::before {
    background: var(--darknessLight) !important;
}
[class^="VerticalFlexingList-"] [class*="LinkBarElementBase-"]:hover {
    background: var(--darknessLighter);
    color: var(--type);
}
[class^="Card-"] {
    background: var(--darknessLight);
    color: var(--type);
}
[class^="PostTime-"] {
    color: var(--type);
}
[class^="PostByline-"] {
    color: var(--typeLight);
}

[class^="AdWrapper-"] {
    background: var(--darknessDark);
}
[class^="AdWrapper-"]::before {
    background: var(--darknessLight);
    color: var(--type);
}
[class^="Dagbladet__Footer-"] {
    background: var(--darknessLight);
    color: var(--type);
}
[class^="Dagbladet__CopyrightContainer-"] {
    background: var(--darknessLighter);
    color: var(--type);
}
}
