css:
/*
RSI Spectrum - Darker
Version: 1.4.4
By: 3of9 (Wizard)
https://userstyles.org/styles/177505
*/

@-moz-document url-prefix("https://robertsspaceindustries.com/spectrum/") {
* {
    --color-notice: #eaa41a;
    --color-motd-background: #474730;
    --color-motd: #DEC342;
    
    --color-0: #020c19;
    --color-1: #080e17;
    --color-2: #141d2a;
    --color-3: #243249;
    --color-4: #354d6f;
    --color-5: #597398;
    
    --text-0: #b3dbfe;
    
    scrollbar-width:thin;
    scrollbar-color: var(--color-5) var(--color-1);
}

.message-item {
    padding-top: 6px;
    padding-bottom: 6px;
}

.sidebar-list .sidebar-item.community {
    padding-top: 2px;
    padding-bottom: 2px;
}

.sidebar-list .sidebar-item {
    padding-top: 1px;
    padding-bottom: 1px;
}

.lobby-member-presences > .members-list .lobby-presence-item {
    padding-top: 2px;
    padding-bottom: 2px;
}

.theme-dark .sidebar-list .sidebar-item.unread .column.content a {
    color: var(--color-notice);
}

.theme-dark .message-item {
    background-color: var(--color-2);
}

.theme-dark .message-item.status-default:not(.edit-mode):hover {
    background-color: var(--color-3);
}

#app.theme-dark #app-content #page.lobby #page-main .messages-list {
    background-color: var(--color-2);    
}

#app.theme-dark #app-content #page.lobby .lobby-footer > .top {
    background-color: var(--color-0);    
}

.theme-dark .message-input .message-row > .input {
    background-color: var(--color-2);
}

#app.theme-dark #app-content #page.lobby .lobby-footer > .message-input {
    background-color: var(--color-3);
}

.theme-dark #page.forum-thread .page-content {
    background-color: var(--color-2);    
}

.theme-dark #sidebar {
    background-color: var(--color-2);
    border-right: 1px solid var(--color-5);
}

#page.forum-channel .page-channels, #page.forum-channel .page-content
{
    background-color: var(--color-2);
}

.theme-dark #page.forum-channel .row.columns-header {
    background-color: var(--color-3);
}

.theme-dark #page.forum-thread .page-content .forum-thread .forum-thread-replies .forum-thread-item .content-block.text {
    color: var(--text-0);
}

.theme-dark .message-media-meta {
    background-color: var(--color-3);
}

.theme-dark #app-content > #page.community > #page-main .community-list {
    background-color: var(--color-2);    
}

.theme-dark #app-content > #page.community > #page-main .community-list h3{
    background-color: var(--color-3);
}

.theme-dark .lobby-motd-body {
    background-color: var(--color-motd-background);
}

.lobby-motd-title strong {
    color: var(--color-motd);
}

.lobby-motd-date {
    color: var(--color-motd);    
}

.lobby-motd-message p {
    color: var(--color-motd);        
}

.theme-dark .lobby-motd-dismiss {
    background-color: var(--color-motd);
}

.lobby-motd-dismiss .icon use {
    fill: var(--color-1)
}
}
