css:
@-moz-document domain("patriots.win"), domain("greatawakening.win") {
html, body, .main-content, .home .container, .search .container, .post-single .container, .profile .container, .page .container, .logs .container, .comment .details a[data-action=minimize] {
	background-color: #323232;	
}

.comment-parent .title {
    color: #42a9fb;
}

.comment-parent .author {
    color: #f3d01f;
}

.post .vote a[data-direction=down] {
    -webkit-tap-highlight-color: transparent;
    display: block;
    margin: 0 auto 5px;
    background-image: url(https://i.imgur.com/IN0VHwl.png);
    background-repeat: no-repeat;
    width: 45px;
    height: 43px;
}

.post-list .content {
    margin: 8px 0 0 174px;
}

.post .vote {
    display: inline-flex;
}

.post .vote .count {
	min-width: 50px;
    font-size: 18px;
    padding-top: 16px;
}

.post .vote a[data-direction=down], .post .vote a[data-direction=up]  {
    font-size: 18px;
    margin-left: 8px;
     margin-top: 0px;
}

.post .vote a[data-direction=up] {
	margin-right: 8px;
    margin-top: 0px!important;
}

.post .vote a[data-direction=down] {
    margin-top: 0px!important;
}

.message-entry.unread {
    background-color: #474747;
    border: 1px solid #090909;
    padding: 0px;
}

.form.reply textarea {
	background-color: #323232;
	color: #fff;
}

.post.stickied {
	background: linear-gradient(#383838,rgba(255,165,0,0.05));
}

.post {
    background: linear-gradient(#383838,#2F2F2F);
}

.post .title:visited {
    color: #c17dff;
}

.post .title {
    color: #67bcff;
	text-shadow: 2px 1px 4px #000;
    line-height: 14px;
}

.post-single .content p {
    text-shadow: 1px 1px 4px #000;
}

.nav-user .inner a:first-child {
    font-weight: 900;
	text-shadow: 2px 1px 4px #000;
}

.post.stickied .title, .nav-message a {
    color: #f00;
    background-color: rgba(255,165,0,0);
}

.total, .post-single .content p, .footer, .footer a, .module .body p, .empty p, .main-content, .post .domain, .sidebar .mods .head p {
	text-shadow: 2px 1px 4px #000;
	color: #fff;	
}

.post .domain {
    font-weight: 400;	
}

.comment {
    border-top: 1px solid #484848;
    border-left: 2px solid #484848;
	background-color: #383838;
}

.content {
    color: #39dc25;
}

.module, .popup .inner {
	background-image: none;
	background-color: #323232;
	box-shadow: 0 0px 0px 0px grey;
}

.popup .subline, .popup .options label, .nav-profile .active, .nav-profile span, .nav-profile {
    color: #fff;
}	

.popup .warning {
    font-size: 14px;
    color: #ff3c3c;
}

.actions [data-action=reply] {
    color: #3eafff;
}

.details a.author {
    color: #ff4949;
}

.sidebar .mods .body a, .sidebar .mods .head a, .sidebar .mods .foot a, .actions .comments, .mod-list a  {
	color: #168be8;	
	text-shadow: 2px 1px 4px #000;
}

.sidebar .mods {
    border: 1px solid;
    border-color: #23489c;	
	background-color: #404040;
}

.module .body .heading {
	color: #168be8;
	text-shadow: 2px 1px 4px #000;
}

.nav-profile p, .nav-user .inner a:first-child {
	color: #f00;	
}

.module .body {
	color: #00a1ff;
	background-color: #404040;
	border: 1px solid;
    border-color: #23489c;
}

.post .share {
    background-color: #323232;
    box-shadow: 0 0 2.75px 0.25px rgba(255, 255, 255, 0.2);
}

.form.edit input[type=submit], .form.edit input[data-action=cancel], .form.reply input[type=submit], .form.reply input[data-action=cancel] {
	background-color: #323232;
	border-color: #ff12126b;
	color: #fff;
}

.sidebar .search input {
	background-color: #323232!important;
    color: #fff!important;
	border: 1px solid;
}

.sidebar .login input[type=text], .sidebar .login input[type=password], .single .form input[type=text], .single .form input[type=email], .single .form input[type=password], .single .form input[type=url], .single .form textarea, .page .main-content .form input[type=text], .page .main-content .form input[type=email], .page .main-content .form input[type=password], .page .main-content .form input[type=number], .page .main-content .form textarea, .popup .form input[type=text], .popup .form input[type=number], .popup .form textarea {
	background-color: #323232!important;
	color: #fff!important;
}

.single .form select {
    background-color: #323232;
	color: #fff!important;
}

.post-single .sort select {
	background-color: #323232;
	color: #fff;
	border: 1px solid;
    border-color: #f00;
}

.single .tags input[type=checkbox]+label {
    color: #fff;
    background-color: #484848;
}

.single .tags input[type=checkbox]:checked+label {
    color: #ffffff;
    background-color: #168be8;
    border: 1px solid #000000;
}

.more a:hover {
    background-color: #444;
    border: 1px solid #ff1d42;
    color: #f5f5fd;
}

.more a {
	background-color: #333;
    border: 1px solid #ff1d42;
}

.content {
    color: #fff;
}

.content a {
	color: #168be8;
	text-decoration: underline;
}

em, i {
    color: #25d442;
}

.nav-user .inner .unread svg {
    color: #00ff0a;
}

.post .vote a[data-direction=up] {
	font-size: 18px;
	margin-bottom: 8px;
}

.post .vote a[data-direction=down] {
	font-size: 18px;
	margin-top: 8px;
}

.comment .vote a[data-direction=up] {
    margin-top: 8px;
    margin-bottom: 14px;
}

a:hover {
	color: #ff0;
}

.header .bar, .nav-user, .module .head, .popup .head {
	background-image: none;	
	background-color: #252525;
}

.module .body {
    border-color: #b30000;
}
.module .body p {
	width: 100%	
}

.home .footer {
	display:none;
}
}
