css:
@-moz-document domain("dhl.de") {
:root {
    --background-color: black;
    --primary-color: #302e2e;
    --font-color: white;
}

body {
    background: var(--background-color);
}

.panel {
    background: var(--primary-color);
}

.panel-title {
    color: var(--font-color) !important;
}

.mm_shipmentHeader {
    background: var(--primary-color) !important;
    color: var(--font-color) !important;
}

.imageTextTeaser__content {
    background: var(--primary-color);
}

.imageTextTeaser__content p,
a,
h5,
.txt {
    color: var(--font-color) !important;
}

.footer {
    background: var(--primary-color) !important;
}

.footer a {
    color: var(--font-color) !important;
}

.header-info {
    background: var(--primary-color) !important;
}

.header-info a,
p {
    color: var(--font-color) !important;
}

.loggedInUserName {
    color: black;
}

.background-wrapper {
    background: black !important;
}

div .header__linkCaption {
    color: black;
}

.header-tab {
    color: black !important;
}

.cookie-hint {
    background: var(--primary-color) !important;
}

.search a {
    color: black !important;
}

.header__navigationDropdown {
    background: var(--primary-color) !important;
}

.sub-menu .header__linkCaption {
    color: var(--font-color);
}

.header__navigationDropdown .header__linkCaption {
    color: var(--font-color) !important;
}

.lang__list {
    background: var(--primary-color) !important;
}

.lang__list-item:hover a {
    color: black !important;
}

h2 {
    color: var(--font-color) !important;
}

h3 {
    color: var(--font-color) !important;
}

h4 {
    color: var(--font-color) !important;
}

.btn {
    color: black !important;
}

.panel-heading {
    background: var(--primary-color) !important;
}

table td {
    background: var(--primary-color) !important;
    color: var(--font-color) !important;
}

table th p {
    color: black !important;
}

strong {
    color: var(--font-color) !important;
}

.checkmark li {
    color: var(--font-color) !important;
}

.text-image.hideable {
    background: var(--primary-color) !important;
}

.stepGuide div {
    background: var(--primary-color) !important;
}

.accordion {
    background: var(--primary-color) !important;
}

.postagecalculator {
    background: var(--primary-color) !important;
}

.imageTextTeaser__content {
    background: var(--primary-color) !important;
}

#shipment-events {
    color: var(--font-color);
}

.popup__container {
    background: var(--primary-color) !important;
}

.dropdown-menu {
    background: var(--primary-color) !important;
}
}
