css:
@-moz-document domain("visir.is") {
body {
	color: #cecece;
    background: #1e1e1e;
}

a {
	color: #cecece !important;
}

h1, h2, h3, h4, h5, h6 {
	color: #cecece;
}

header.main-header>.limit {
    background: #181818;
}

footer.main-footer {
    background-color: #3e3e3e;
}

footer.main-footer h3 {
    color: #c0c0c0;
}

.segment {
    background: inherit;
}

.segment.-bg-gray {
    background-color: #1e1e1e;
}

.articles-list.-bg-gray {
    background-color: #1e1e1e;
}

.segment.-bg-beige {
    background-color: #1e1e1e;
}

.articles-list {
    background: inherit;
}

time.article-item__time {
    color: #3868be;
}

.main-menu__icon {
    border-left: .1rem solid #1e1e1e;
}

.main-menu__icon>a [class*=icon-] {
    color: inherit;
}

header.main-header .main-header__upper * {
    color: #fff !important;
}

.tagslist .tag {
    background: inherit;
}

/* Sidebar  */
.offscreen-panel--mobile-nav .offscreen-panel__content {
    background-color: #181818;
}

.mobile-nav {
    background: inherit;
}

.mobile-nav__item {
    background: inherit;
}

.mobile-nav .search-box input {
    background: inherit;
}

.search-box .search-box__button i:before {
    color: inherit;
}

.search-box input {
    color: #fff !important;
}


/* Scrollbars  */

/* width */
::-webkit-scrollbar {
  width: 10px;
}

/* Track */
::-webkit-scrollbar-track {
  background: #181818; 
}
 
/* Handle */
::-webkit-scrollbar-thumb {
  background: #3868be;
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: #555; 
}

}
