css:
@-moz-document domain("chanind.github.io") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #2a7ae2 #1c1c1c;
}
.site-title,.site-nav .page-link,.home h2,.rss-subscribe,.footer-heading,.post-list li a:hover{
    color: #fff !important;
}
.post-title{
    color: #fff;
}
.post-content{
    color: #fff;
}
.highlighter-rouge .highlight{
    background: #505050;
}
code,pre{
    background: #505050;
}
.highlighter-rouge{
    background: #eef;
    color: #000;
}
.site-header{
    border-top: 0;
}
.footer-heading:after{
    content: " | Dark theme by Maciejozaman";
    color: #2a7ae2;
}
}
