@-moz-document domain(maxxpoint.westipc.com) {

html, input, textarea {
    color:#000
}

option {
    color:#FFF!important
}

html:not(#a), option {
    background-color:#323639!important 
}

html:not([stylus-iframe]):after {
    content:'';
    background:#3236394D!important;
    position:fixed!important;
    left:0;
    top:0;
    right:0;
    bottom:0;
    z-index:-2147483647;
    pointer-events:none;
 }

input, textarea {
    background:#FFF                                     
}
video {
    background-color:#000
}
html:not([stylus-iframe]) {
    background-size:100% auto!important;
    background-position:center center!important;
    background-repeat:no-repeat!important;
    background-attachment:fixed!important
}

html, body {
    min-height:100vh!important                                    
}
@media(max-aspect-ratio:16/9) {
    html:not([stylus-iframe]) {
        background-size:auto 100%!important
    }
}

html, html:after, img, embed, video, canvas, option, object, frame, :fullscreen:not(iframe), iframe:not(:fullscreen), body frameset, body>*[style*="l("]:not([style*="sor:"]):not([type="text"]) {
    filter:invert(1)hue-rotate(180deg)!important
}



iframe, body, :fullscreen, body>:not([src]):empty, body>*>:not([src]):not([href]):empty, body footer, body footer>:not([src]):not([href]), body>[id*="ooter"], body>[id*="ooter"]>:not([src]):not([href]), body>div>[id*="ooter"], body>div>[id*="ooter"]>:not([src]):not([href]), body>[class*="ooter"], body>[class*="ooter"]>:not([src]):not([href]), body>div>[class*="ooter"], body>div>[class*="ooter"]>:not([src]):not([href]) {
    background:none!important
}

body footer :not(#a), body>[id*="ooter"] :not(#a), body>div>[id*="ooter"] :not(#a), body>[class*="ooter"] :not(#a), body>div>[class*="ooter"] :not(#a) {
    color:#999!important          
}



