.ui-state-highlight, .ui-widget-content, .ui-widget-header, .ui-jqgrid {
    border: 1px solid #dddddd;
    background: #fff 50% 50% repeat-x;
	margin-top: 0px;
    color: #444444;
    text-shadow:0 0 .1px
}

.ui-jqgrid div {
    white-space: normal !important;
    background: #fff;
    height: auto;
    vertical-align: text-top; /*
    padding-top: 0px; */
    text-shadow:0 0 .1px
}

.ui-state-default {
    border: 1px solid #dddddd;
    background: #fff 50% 50% repeat-x;
    font-weight: bold; /*
    margin-top: 0px;  */
    color: #444444;
    text-shadow:0 0 .1px

}


