css:
@-moz-document domain("www.usgamer.net") {
body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #FFBA00 #1c1c1c;
}
/*HEADER*/
.header-desktop,.header-desktop .breaking,.header-desktop .navigation .primary,.header-desktop .navigation .secondary,.page{
    background: #1c1c1c;
}
.header-desktop .breaking strong,.header-desktop .navigation .primary span,.header-desktop .navigation .secondary span{
    color: #fff;
}
.header-desktop .breaking a,.header-desktop .breaking{
    color: #FF5C57;
}
/*HEADER*/
.title a,.subtitle{
    color: #fff !important;
}
.article-stack-item.large .details{
    background: #122779;
}
.article-stack-item.medium .details{
    background: #FFBA00;
}
.article-stack-item.medium .title a,.article-stack-item.medium .subtitle{
    color: #122779 !important;
}
.byline{
    background: #FFBA00;
}
.article-list.hd .details > *{
    color: #000;
}
/*NEWSLATER*/
.newsletter-promo .title{
    color: #fff;
}
.form .input-and-button input{
    background: transparent;
    color: #fff;
}
.form .input-and-button input::placeholder{
    color: #fff;
}
.form label{
    color: #FFBA00 !important;
}
/*NEWSLATER*/
/*STARTING-SCREEN*/
.starting-screen-about .details,.starting-screen-about,.starting-screen-about .cover,.starting-screen-about .title{
    background-color: #FFBA00;
}
.starting-screen-about .button{
    margin-top: 0;
}
/*STARTING-SCREEN*/
/*POPULAR-GAMES*/
.popular-games-item .subtitle a{
    color: #FFBA00;
}
/*POPULAR-GAMES*/
/*PODCAST*/
.podcast-promo .title{
    color: #42A333;
}
/*PODCAST*/
/*GAME-IN-ARTICLE*/
.related-games .section-title{
    color: #FFBA00;
}
.related-games-item ul li a{
    color: #fff;
}
.related-games-item{
    border-right-color: #FFBA00;
}
/*GAME-IN-ARTICLE*/
/*AUTHOR*/
.author-bio .name a{
    color: #D6D9DC;
}
/*AUTHOR*/

/*MORE*/
.trending-bar{
    background: #1c1c1c;
    border-top: 3px solid #FFBA00;
}
.section-title{
    color: #FFBA00;
}
.trending-item .title{
    color: #fff;
}
/*MORE*/
/*LOGIN*/
.sign-in-overlay .sign-in-content > div{
    background: #1c1c1c;
    border: 2px solid #FFBA00;
}
.form input{
    color: #fff;
    background: transparent;
    border-color: #FFBA00;
}
.button.outline{
    color: #FFBA00;
}
/*LOGIN*/
/*SEARCH*/
.page-title{
    color: #FF5C57;
}
.page-title::after{
    background: #FF5C57;
}
.search-form input{
    background: transparent;
    border-color: #FF5C57;
    color: #fff;
}
.search-form input::placeholder{
    color: #FF5C57;
}
.tabs .on > span{
    color: #158DDE;
}
.tabs a > span{
    background: transparent;
    color: #fff;
}
.alert{
    color: #FF5C57;
}
/*SEARCH*/
/*PICTURE*/
.lightbox-overlay{
    background: transparent;
}
/*PICTURE*/
/*BUTTONS*/
.button.disabled{
    background: #FFBA00;
}
/*BUTTONS*/
/*FOOTER*/
.footer.hd .footer-container:after{
    content: "Dark theme by Maciejozaman";
    color: #FFBA00;
    margin-left: 5px;
}
/*FOOTER*/
}
