css:
@-moz-document domain("stackoverflow.blog") {
:root, body.home {
    background-color: #222;
}
body, body.home {
    color: #666;
}
.site-header, .intro-header {
    background-color: #1c1c1c;
}
.site-header__nav a:hover {
    background-color: #333;
    color: #888;
}
.site-header__nav a.active:hover {
    background-color: #333;
}
.site-header__nav a.active {
    background-color: hsl(28, 75%, 40%);
}
.intro-header__social::before,
.author.archive .contributor-card {
    border-color: #333;
}
.section-title, .m-post-card__title a {
    color: #777;
}
.section-title--orange-underline::after {
    background-color: hsl(28, 100%, 35%);
}
.button.-orange-fill {
    background-color: hsl(28, 80%, 35%);
    color: #ccc;
}
.button.-orange-fill:hover {
    background-color: hsl(28, 80%, 45%);
    color: #ddd;
}
.posted-on-container {
    color: #505050;
}
.posted-on-container--avatar a,
.m-post-content a,
.m-author__meta-item {
    color: hsl(205, 100%, 30%);
}
.posted-on-container--avatar a:hover,
.m-post-content a:hover,
.m-author__meta-item:hover,
.m-post-card__title a:hover {
    color: hsl(205, 100%, 35%);
}
.m-tag-list__link {
    background-color: #333;
    color: #666;
}
.m-tag-list__link:hover {
    background-color: #383838;
    color: #ccc;
}
figure.wp-block-image,
.related-articles .has-post-thumbnail img.wp-post-image,
.m-post-card__image img {
    opacity: .4;
}
.m-post-card__image {
    border-color: #888;
}
.bg-black-025 {
    background-color: #1f1f1f !important;
    border-color: #333 !important;
}
.intro-header a,
.comments-area a,
a.mainsite {
    color: hsl(28, 100%, 35%);
}
.intro-header a:hover,
.comments-area a:focus,
.comments-area a:hover,
a.mainsite:hover {
    color: hsl(28, 100%, 45%);
}
.button.-primary {
    border-color: hsl(205, 100%, 30%);
    color: hsl(205, 100%, 35%);
}
.button.-primary:hover {
    background-color: hsl(205, 100%, 25%);
    border-color: hsl(205, 100%, 35%);
    color: #aaa;
}
input, select, textarea {
    background-color: #333;
    border-color: #444;
}
.comment-form > .form-submit > .submit {
    background-color: #222;
    border-color: hsl(28, 100%, 35%);
    color: hsl(28, 100%, 35%);
}
.comment-form > .form-submit > .submit:hover {
    background-color: hsl(28, 100%, 7%);
    border-color: hsl(28, 100%, 40%);
    color: hsl(28, 100%, 40%);
}
.m-footer-cta, .m-footer-cta__title {
    color: #ccc;
}
.button.-white {
    color: #ccc;
}
.button.-outline {
    border-color: #ccc;
}
.footer-left-cta, .footer-left-cta::before {
    background-color: hsl(205, 100%, 25%);
}
.footer-ctas-wrapper {
    background: linear-gradient(to right,hsl(205, 100%, 25%) 0,hsl(205, 100%, 25%) 50%,hsl(28, 75%, 30%) 50%);
}
.footer-right-cta, .footer-right-cta::before {
    background-color: hsl(28, 75%, 30%);
}
.site-footer {
    background-color: #282828;
}
.site-footer .-title a {
    color: #555;
}
.site-footer .-title a:hover {
    color: #888;
}
.site-footer .-link {
    color: hsl(205, 45%, 42%);
}
.site-footer .-link:hover {
    color: hsl(205, 45%, 60%);
}
}
