css:
@-moz-document url-prefix("http://trade.aliexpress.com/order_detail.htm"), url-prefix("https://trade.aliexpress.com/order_detail.htm") {

#records-section > .ui-tab-content {
    display: none !important;
}

#fund-pnl,
#operate-pnl {
    display: block !important;
}

#header, 
.top-banner-container, 
.site-footer, 
.footer-copywrite, .ui-step, 
.order-operate, 
.extern-info, 
.confirm-receiving,
#top-lighthouse, 
#J_xiaomi_dialog, 
#J_AlipriceExtension_AliPrice  {
    display: none;
}

body {
    min-width: auto;
}

div.me-body-container{
    width: auto;
}

.remark{
    display: none;
}

span.im-contact-seller{
    display: none;
}

td.shipping{
    display: none;
}

/* .shipping-table col.name,  */
/* .shipping-table col.no,  */
.shipping-table col.remark, 
.shipping-table col.detail {
    width: auto;
}


.fund-table col.shipping-price, 
.fund-table col.change-price, 
.fund-table col.discount-price, 
.fund-table col.order-price {
    width: auto;
}

.fund-table col.pay-c1, 
.fund-table col.pay-c2, 
.fund-table col.pay-c3, 
.fund-table col.pay-c4 {
    width: auto;
}

/*.product-table col.baobei, */
.product-table col.price, 
.product-table col.quantity, 
.product-table col.amount, 
.product-table col.trade-status, 
.product-table col.shipping {
    width: auto;
}

}
