css:
@-moz-document domain("thedonald.win") {
.sidebar .img, .module .body img, .home .footer, .sidebar .submit-link:after, .sidebar .submit-text:after, .header .bg, .module .head, .popup .head, .sidebar .module .body, .footer p:first-of-type{
	display: none;	
}

.wrapper.home {
    background: none;
}

article, aside, details, figcaption, figure, footer, header, hgroup, menu, nav, section {
    display: contents;
}

.header .bar, .nav-user, .sidebar .submit-link, .sidebar .submit-text, .more a, .more a:hover {
	background-color: #323232;
	background-image: none;
	color: #fff;
}

.post.stickied .title, .post .domain, .actions .comments, .actions [data-action=reply], .details a.author, .mod-notes_author, .post .title:visited, .post .title, .vote[data-vote=up] a[data-direction=up], .vote[data-vote=up] .count{
	background: none;
	color: #000;
	text-shadow: none;
}
}
