@-moz-document domain("jigglypuffsdiary.com") {
html,
body {
    background-color: #222222;
}
header.site-header {
    background-color: #333333;
}
body,
section,
h1,
h2,
h3,
h4,
h5,
h {
    color: #d4d4d4;
}
.nav-previous a,
.nav-next a {
    background-color: inherit;
}
section.widget *,
section.widget h2.widget-title,
header.site-header .site-title a {
    color: #777777;
}
div.entry-content p {
    font-family: "/*[[jpdfont]]*/", sans-serif !important;
    font-size: /*[[jpdsize]]*/ !important;
}
span[class*="ezoic"],
span[class*="ezoic"][style*="display:block"],
.adtester-container-127.adtester-container.box-3.ezoic-ad,
#div-gpt-ad-jigglypuffsdiary_com-box-1-0,
.adtester-container-126.adtester-container.box-1.ezoic-ad,
.ai-viewport-2.ai-viewport-1.code-block-2.code-block,
#ezmobfooter {
    display: none !important;
    visibility: hidden !important;
    height: 17px !important;
    max-height: 17px !important;
    min-height: initial !important;
}
}
