css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("briefcase.yahoo.co.jp") {

html {
background: #080808 repeat-x url(data:image/png;base64,) !important;
color: #FFF !important;
} 
body {
background: transparent !important;
margin-top: 10px !important;
} 

tr, td, table[bgcolor="#ffffff"] {background: none !important;
}
table[bgcolor="#999999"] {
background: #000 !important;
border: 1px #3A3A3A solid !important;
}
table[cellspacing="1"] {
background-color: #303030 !important;
}
tr[bgcolor="#cccc99"] {background: none !important;
}
tr[bgcolor="#dcdcdc"] {background: #080808 !important;
}
a:link {color: #E0E0E0 !important;
} 
a:visited {color: #B2B2B2 !important;
}
font[size="-1"] {
font-size: 10px !important;
color: #CCC !important;
}
font[color="#ff9933"], font[color="#ff6600"] {color: #FFF !important;
}
img[src="http://i.yimg.jp/i/jp/bc/sfld.gif"],
img[src="http://i.yimg.jp/i/jp/bc/folder.gif"] {
background: url(data:image/png;base64,) !important;
width: 0 !important;
padding-left: 16px !important;
margin-left:-1px !important;
}
img[src="http://i.yimg.jp/i/jp/bc/bc_main.gif"] {
background: url(data:image/png;base64,) !important;
background-repeat: no-repeat !important;
width: 0 !important;
padding-left: 310px !important;
}
input[src="http://i.yimg.jp/i/jp/bc/bot_pt2.gif"] {
background: url(data:image/png;base64,) !important;
background-repeat: no-repeat !important;
width: 0 !important;
padding-left: 115px !important;
margin-bottom: -4px !important;
}
input[src="http://i.yimg.jp/i/jp/bc/sbot_mov.gif"] {
background: url(data:image/png;base64,) !important;
background-repeat: no-repeat !important;
width: 0 !important;
padding-left: 35px !important;
}
input[src="http://i.yimg.jp/i/jp/bc/sbot_cop.gif"] {
background: url(data:image/png;base64,) !important;
background-repeat: no-repeat !important;
width: 0 !important;
padding-left: 35px !important;
}
input[src="http://i.yimg.jp/i/jp/bc/sbot_del.gif"] {
background: url(data:image/png;base64,) !important;
background-repeat: no-repeat !important;
width: 0 !important;
padding-left: 35px !important;
}

#uhdMain,
table[bgcolor="#ffffcc"],
a[href*="http://ard.yahoo.co.jp/"] {
display: none !important;
}
}
