css:
@-moz-document domain("redcafe.net") {
body{
    max-width: 1000px;
    margin: 0 auto;
}

html{
    background: url(https://i.imgur.com/edzCSEY.jpg) repeat;
    font-family: 'Segoe UI','Helvetica Neue',Helvetica,Roboto,Oxygen,Ubuntu,Cantarell,'Fira Sans','Droid Sans',sans-serif;
}


.structItem-title{
    font-size: 17px;
    font-weight: 300;
}

.message-name{
    font-size: 17px;
    font-weight: bold;
} 

.p-body, .p-body-header{
    background: #faf4f4;
}

.p-body-header{
border-radius: 0;
}

.uix_headerContainer, .block, .breadcrumb{
    background: #faf4f4;
}


.badge--highlighted, .badge{
    background:#ffffff;
    color: #c20000;
    font-weight: bold;
}

.badge.badge--highlighted, .badgeContainer.badgeContainer--highlighted::after {
    display: inline-block;
    color: #fefefe;
    background: #3d793f;
    border: 1px solid #1c1c1c00;
}

.u-dt{
    color:#c20000;
}

.message--post{
    border-top:10px solid black;
}    

.uix_nodeList{
    width: 700px;
}


.block--latest-posts-widget.block > .block-container{
    width: 700px;
    border-radius:4px;
}

.p-body-sidebar{
    width: 235px;
    margin-right:0px;
}


.uix_extendedFooter, .uix_extendedFooterRow .block{
    background:#c20000;
    color: #ffffff;
}

.block--category .block-header, block-header js-nodeMain {
    background:#faf4f4;
    color: #464646;
    font-weight: 700;
    font-size:22px;
}

.block-minorHeader.uix_threadListSeparator {
    display: flex;
    align-items: center;
    font-size: 1.3rem;
    color: #ffffff;
    background:#c20000;
    border-top: 2px solid    #e0e0e0;
}

.is-unread .structItem-title {
    font-weight: 900;
}

.structItemContainer .structItemContainer-group--sticky .structItem--thread {
    border-left: 3px solid #000000;
}

.message-cell.message-cell--user, .message-cell.message-cell--action {
    position: relative;
    font-size: 1.5rem;
    background: #f2f1f0cc;
    border-right: 1px solid #e0e0e0;
    min-width: 0;
    color: #000000ba;
    font-family: segoe ui;
}

.menu, .menu--structural, .menu--left, .is-active is-complete{
    z-index:5000;
}

.p-breadcrumbs{
    z-index:1000;
}

.block--messages .message .message-attribution--split, .js-quickReply .message .message-attribution--split, .block--messages .block-row .message-attribution--split, .js-quickReply .block-row .message-attribution--split {
    color:#eee;
background-color:#313131;
    padding: 4px 10px;
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
}


span.username{
    font-size:11px;
}

.bbCodeBlock-content .bbImage{
    width:20%;
}

.fauxBlockLink-blockLink{
    font-weight: bold;
    font-size:14px;
}

.badge.badge--highlighted, .badgeContainer.badgeContainer--highlighted::after {
    display: inline-block;
    color: #c20000;
    background:#ffffff;
    font-weight:bold;
}

.block--latest-posts-widget .structItem-cell.structItem-cell--meta  {
display: none;
}

.structItem-cell.structItem-cell--meta  {
    background: transparent;
    border: 0px;
}

.block-tabHeader {
background:#c61818;
    color:#fff;
}

.block-tabHeader .tabs-tab {
background:    #c61818;
color:#fff;
}

.block-tabHeader .tabs-tab.is-active {
    color:#fff;
   
}

.block-tabHeader .tabs-tab.tabs-tab--gapLeft {
    border-left: 0px solid     #f9f6f6;
        border-left-color: rgb(236, 234, 234);
    border-radius: 0;
}

.block-tabHeader .tabs-tab.tabs-tab--gapRight {
    border-right: 0px solid 
    #7b7b7b;
    border-radius: 0;
}

.actionBar-set.actionBar-set--internal {
    opacity: 0.5;
}

.node-stats {
    display: inline-flex;
    align-items: center;
    width: 140px;
    min-width: 140px;
    vertical-align: middle;
    text-align: center;
    padding: 10px 0;
    opacity: 0.4;
}

.label {
    display: inline-block;
    padding: 1px .35em;
    border: 1px solid 
    transparent;
        border-top-color: transparent;
        border-right-color: transparent;
        border-bottom-color: transparent;
        border-left-color: transparent;
    border-radius: 4px;
    font-size: 80%;
    line-height: 1.26;
    text-decoration: none;
}

.node-extra .node-extra-title {
    color:#4f4e4e;
    font-weight:400;
    opacity:0.6;
}

.p-nav{
    font-size:12px;
}

.p-nav-list .p-navEl {
    font-size: 12px;
    color: 
    rgba(255,255,255,0.7);
    font-weight: 500;
    text-decoration: none;
    text-transform: uppercase;
    line-height: 1;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    height: 40px;
    -webkit-transition: all .2s ease;
    transition: all .2s ease;
}

.node-subNodeMenu{
    display:none;
}

.node-description{
    font-size:12px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  max-width: 200px;
    font-family: segoe ui;
    }

.block block--messages{
    min-width: 900px;
}

.block--latest-posts-widget .block-container {
    padding: 0px;
    background: transparent;
    box-shadow: 0px 0px 0px 0    rgba(0,0,0,0.2);
}

.node, .node--id6 node--depth2, .node--forum node--unread {
    list-style: none;
    margin: 0;
    padding: 0;
    box-shadow: 1px 1px 2px 0    rgba(0,0,0,0.2);
}

.uix_nodeList .block-body {
    box-shadow: 1px 1px 2px 0     rgba(0, 0, 0, 0);
}

.block[data-widget-id] .block-minorHeader, .p-body-sideNav .block-minorHeader, .p-body-sidebar .block-minorHeader, .uix_extendedFooterRow .block-minorHeader, .columnContainer-sidebar .block-minorHeader, .block[data-widget-id] .block-header, .p-body-sideNav .block-header, .p-body-sidebar .block-header, .uix_extendedFooterRow .block-header, .columnContainer-sidebar .block-header {
    display: flex;
    align-items: center;
    padding: 16px;
    font-weight: 500;
    border-bottom: 1px solid 
    #dbdbdb;
}

.pageNav-page {
background: #fafafa;
color:#7b7b7b;
border-radius: 0px;
border: 1px solid   #dbdbdb;
    white-space: nowrap;
    height: 10px;
}

.structItem--thread:hover{background-color:#f7f5f5}

.actionBar-set.actionBar-set--external {
    float: right;
    position: relative;
    margin-right: -3px;
    margin-top: -45px;
    font-family: segoe ui;
    font-size: 14px;
    opacity: 1;
    padding-right:0px;
    padding-top:10px;
    color: #d70101;
    z-index: 999999999;
}

.actionBar-set a:link{
    color: #b50808;    
}

.message-actionBar{
    max-height:5px;
}

.actionBar {
    border-top: 0px solid     #dbdbdb;
    padding: 16px;
    margin: -16px;
        margin-top: -16px;
    margin-top: 0;
}

.message-cell {
    display: block;
    vertical-align: top;
    padding-top: 16px;
    padding-left: 16px;
    padding-right: 16px;
    padding-bottom: 5px;
}

.memberHeader-name span.username{
    font-size:25px;
}

.p-header .p-header-logo img {
    width: 300px;
}


element {

}
body .uix_searchBar .uix_searchBarInner .uix_searchForm {

    display: inline-flex;
    align-items: center;
    transition: ease-in flex-grow .3s,ease-in max-width .3s,ease-in background-color .2s;
    flex-grow: 0;
    max-width: 250px;
    width: 100%;
    pointer-events: all;
    position: relative;
    color: #555;
    background:    #f5f5f5;
    border-radius: 4px;

}

.uix_newIndicator{
opacity:0.3;
}

.structItem:nth-child(2n) {
    background-color: 
    #f4ede8a3;
}

.block--messages .message, .js-quickReply .message, .block--messages .block-row, .js-quickReply .block-row {
    color:#101010;
background:#fafafa;
border-width: 0;
border-style: solid;
border-top-color: #d4d4d4;
border-right-color: #c7c7c7;
border-bottom-color: #bababa;
border-left-color: #c7c7c7;
border-radius: 3px;
border: 1px solid    #dbdbdb;
        border-top-width: 0px;
    border-top-width: 0;
    box-shadow: 0;
}

.block--messages .message .message-attribution--split, .js-quickReply .message .message-attribution--split, .block--messages .block-row .message-attribution--split, .js-quickReply .block-row .message-attribution--split {
    color: #464646;
background-color: #cc0404;
    padding:5;
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
}

.block[data-widget-id] .block-minorHeader, .p-body-sideNav .block-minorHeader, .p-body-sidebar .block-minorHeader, .uix_extendedFooterRow .block-minorHeader, .columnContainer-sidebar .block-minorHeader, .block[data-widget-id] .block-header, .p-body-sideNav .block-header, .p-body-sidebar .block-header, .uix_extendedFooterRow .block-header, .columnContainer-sidebar .block-header {
    display: flex;
    align-items: center;
    padding: 5px;
    font-weight: 500;
    font-size:18px;
    border-bottom: 1px solid #dbdbdb;
    background: #faf4f4;
    color: #646464;
}

.block--messages .message, .js-quickReply .message, .block--messages .block-row, .js-quickReply .block-row {
    color: 
#101010;
background:
