}

.bbWrapper{
    font-size:15px;
    font-family: 'Segoe UI','Helvetica Neue',Helvetica,Roboto,Oxygen,Ubuntu,Cantarell,'Fira Sans','Droid Sans',sans-serif;
}

.block-body {
    list-style: none;
    margin: 0;
    padding: 0px;
    border: 0px solid #e3e3e3;
}

.uix_nodeList .block-body {
    border-radius: 0px;
    padding: 0px;
}

.structItem-latestDate{
        font-size: 16px;
}

.structItem-cell {
    display: table-cell;
    vertical-align: top;
    padding: 8px 8px;
}

.message--post .message-cell{
    padding:10px;
