css:
@-moz-document domain("redcafe.net") {
body{
    width: 90%;
    margin: 0 auto;
}

html{
    background: url(https://i.imgur.com/edzCSEY.jpg) repeat;
}

.structItem-title{
    font-size: 17px;
    font-weight: 300;
}

.message-name{
    font-size: 17px;
    font-weight: bold;
} 

.p-body, .p-body-header{
    background: #faf4f4;
}

.uix_headerContainer, .block, .breadcrumb{
    background: #faf4f4;
}


.badge--highlighted, .badge{
    background:#ffffff;
    color: #c20000;
    font-weight: bold;
}

.message-avatar-wrapper{
    display:none;
}

.u-dt{
    color:#c20000;
}


.message--post{
    border-top:10px solid black;
}    


.uix_extendedFooter, .uix_extendedFooterRow .block{
    background:#c20000;
    color: #ffffff;
}

.block--category .block-header{
    background:#c20000;
    color: #ffffff;
}

.block-minorHeader.uix_threadListSeparator {
    display: flex;
    align-items: center;
    font-size: 1.3rem;
    color: #ffffff;
    background:#c20000;
    border-top: 0px solid    #e0e0e0;
}

.is-unread .structItem-title {
    font-weight: 1000;
}

.structItemContainer .structItemContainer-group--sticky .structItem--thread {
    border-left: 3px solid #000000;
}

.message-cell.message-cell--user, .message-cell.message-cell--action {
    position: relative;
    font-size: 1.5rem;
    background: #f2f1f0cc;
    border-right: 1px solid #e0e0e0;
    min-width: 0;
    color: #000000ba;
    font-family: segoe ui;
}

.menu, .menu--structural, .menu--left, .is-active is-complete{
    z-index:5000;
}

.p-breadcrumbs{
    z-index:1000;
}

.block--messages .message,.js-quickReply .message,.block--messages .block-row,.js-quickReply .block-row{
color:#191e1e;
background:#fff;
border-width:0;
border-style:solid;
border-top-color:#e0e0e0;
border-right-color:#e0e0e0;
border-bottom-color:#e0e0e0;
border-left-color:#e0e0e0;
border-radius:4px;
border-radius:2px;
box-shadow:0 0 2px 0 rgba(0,0,0,0.14),0 2px 2px 0 rgba(0,0,0,0.12),0 1px 3px 0 rgba(0,0,0,0.2);
border-top:6px solid #c20000
}

span.username{
    font-size:11px;
}

.fauxBlockLink-blockLink{
    font-weight: bold;
    font-size:14px;
}

.badge.badge--highlighted, .badgeContainer.badgeContainer--highlighted::after {
    display: inline-block;
    color: #c20000;
    background:#ffffff;
    font-weight:bold;
}
}
