@-moz-document domain("locajogos.com") {
/* Topo */
div[class*="topo"],
nav,
.pages-nav.content {
    font-family: 'Open Sans', sans-serif;
    background-color: #222 !important;
    box-shadow: 0 0 5px #757575 !important}

/* Fundo */
body {
    margin-top: 50px;
    background: #E3E3E3;
    background: -moz-linear-gradient(left, #d5d5d5 -25%, #e9e9e9 50%, #d5d5d5 75%);
    background: -webkit-linear-gradient(left, #d5d5d5 -25%, #e9e9e9 50%, #d5d5d5 75%);
    background: linear-gradient(to right, #d5d5d5 -25%, #e9e9e9 50%, #d5d5d5 75%);
}

/* Catálogo Topo */
div [class="alert alert-info text-center"]
    {font-family: 'Open Sans', sans-serif; font-weight: 700; letter-spacing: 1px; color:#f1f1f1; background-color:#1B5C9E; border-radius: 5px; border-color:#002d5b !important;}

/* Roleta e Mimosa Topo */
div [class="alert alert-info text-center col-lg-12"]
    {font-family: 'Open Sans', sans-serif; font-weight: 700; letter-spacing: 1px; color:#f1f1f1; background-color:#1B5C9E; border-radius: 5px; border-color:#002d5b !important;}

/* Pesquisa */
div [class="input-group col-sm-5"]
{ width: 70%; margin-top: 5px; margin-bottom: 10px; margin-left: 26px; margin-right: 0px;}
        
div [class="col-sm-3"] 
{ width: 25%; margin-top: 2px; margin-bottom: 10px; margin-left: 0px; margin-right: 0px; padding: 3px 12px;}

[class*="border"],
div,
::before,
::after {
    border-color: #9a9a9a !important }

/* Botão Ajuda */
#btnAjuda {
	font-weight: 700;
    letter-spacing: 1px;
    display: inline-block;
    padding: 0px;
	color: #ffffff;
	background-color: #1B5C9E;
	width: 110px;
	height: 110px;
	line-height: 100px;
	border-radius: 50%;
	border: double 9px #ffffff;
	text-align: center;
	vertical-align: middle;
	overflow: hidden;
	transition: .9s; }
/* Botão Ajuda End */

#content-wrapper,
.content-wrapper,
.content-wrapper::after,
.article,
.flex_box > div,
.Box-header,
.select-menu-header,
.modal,
