}
#divMateria {
    background-color: black;
    /*height:400px;
	*/
}
.materiaContainer {
    margin: 2em 0;
    padding: 0.4em;
    /*overflow:hidden;
	*/
    background-color: #d0d4b6;
    max-height: 400px;
}
.materiaDivImagem {
    background-color: black;
    padding-top: 2em;
    padding-bottom: 2em;
