    vertical-align: middle;
}
.AlturaBase {
    margin-top: 10px;
}
.GridMinhasContas {
    width: 80px;
    height: 100px;
}
/*Box Jogo*/
figure.snip0078 {
    color: #414141;
    border-radius: 6px;
    background-color: #cfcfcf;
    position: relative;
    float: right;
    margin-top: 15px;
    /*Botões Favorito e Jogado*/
    margin-bottom: 16px;
    margin-left: 20px;
    /* Tamanho Box */
    width: 252px;
    height: 360px;
}
/* Capa Jogo */
figure.snip0078 img.cardCapa {
    opacity: 1;
    max-width: 100%;
    border: 10px solid #000000;
    -webkit-transition: all 0.3s;
    transition: all 0.3s;
    -webkit-transform: scale(0.90);
    transform: scale(0.90);
    -webkit-transform-origin: 0 0;
    transform-origin: 0 0;
    /*Posição Capa*/
    margin-top: -20px;
    margin-left: 20px;
    /*Sombra Capa*/
    box-shadow: 0 1px 4px 1px #40404038;}
    /*Categoria Jogo*/
    div [class="h6"]
    {font-family: 'Open Sans', sans-serif; text-align: center; font-size: 13px; color: #414141;}

/* Platina, Crossbuy e Sombra Jogo */
figure.snip0078 figcaption {
    bottom: 0px;
    right: 0%;
    position: absolute;
    background: #000000;
    padding: 0px;
    -webkit-transition: all 0.2s;
    transition: all 0.2s;
    -webkit-transform: translateY(0);
    transform: translateY(0);
    box-shadow: 0 9px 9px -6px #4040406E;
    /*Posicionamento*/
    width: 100%;
    height: 21%;
}

/* Favorito e Jogado */

/* Favorito Vermelho */
div [class="btn-sm btn btn-danger"]
    {height: 30px; width: 75px; position: relative; left: 15%; margin-bottom: 8px;
    color: #fff; background-color: #d9534f; border-color: #bd1711; border-radius: 4px;}

/* Favorito Verde */
div [class="btn-sm btn btn-success"]
    {height: 30px; width: 75px; position: relative; left: 15%; margin-bottom: 8px;
    color: #fff; background-color: #47a447; border-color: #287928; border-radius: 4px;}

/* Jogado Azul */
div [class="btn-sm btn btn-primary"]
    {height: 30px; width: 75px; position: relative; left: 15%; margin-bottom: 8px;
    color: #fff; background-color: #3276b1; border-color: #1c4d78; border-radius: 4px;}

/* Jogado Verde */
div [class="btn-sm btn btn-success"]
    {height: 30px; width: 75px; position: relative; left: 15%; margin-bottom: 8px;
    color: #fff; background-color: #47a447; border-color: #287928; border-radius: 4px;}

figure.snip0078 figcaption h5,
figure.snip0078 figcaption p {
    margin: 0;
    color: #D70F0F;
}
figure.snip0078 figcaption h5 {
    font-weight: 400;
    /*text-transform: uppercase;*/
}
figure.snip0078 figcaption h5 span {
    font-weight: 700;
}
figure.snip0078 figcaption p {
    font-size: 0.9em;
    font-weight: 500;
    opacity: 0.65;
}
figure.snip0078 a {
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    position: absolute;
    z-index: 1;
}
figure.snip0078.blue figcaption {
    border-radius: 6px;
    background: #CFCFCF00;
}
figure.snip0078.blue img.cardCapa {
    border: 9px solid #0e81f0;
    border-radius: 6px;
}
figure.snip0078.bluelight figcaption {
    background: #4A89CA;
}
figure.snip0078.bluelight img.cardCapa {
    border: 4px solid #4A89CA;
}
figure.snip0078.green figcaption {
    background: #149035;
}
figure.snip0078.green img.cardCapa {
    border: 4px solid #149035;
}
figure.snip0078.red figcaption {
    background: #8a1c0c;
}
figure.snip0078.red img.cardCapa {
    border: 4px solid #8a1c0c;
}
figure.snip0078:hover figcaption,
figure.snip0078.hover figcaption {
    -webkit-transform: translateY(-8px);
    transform: translateY(0px);
    box-shadow: 2px 16px 16px -12px #00000078;
}
figure.snip0078:hover.blue img.cardCapa,
figure.snip0078.hover.blue img.cardCapa {
    border: 4px solid #0e81f0;
    border-radius: 6px;
}
figure.snip0078:hover.bluelight img.cardCapa,
figure.snip0078.hover.bluelight img.cardCapa {
    border: 6px solid #a3bdd8;
}
figure.snip0078:hover.green img.cardCapa,
figure.snip0078.hover.green img.cardCapa {
    border: 6px solid #48c02b;
}
figure.snip0078:hover.red img.cardCapa,
figure.snip0078.hover.red img.cardCapa {
    border: 6px solid #f71f00;
}
 
