/*BADGES*/
.shape {
    border-style: solid;
    border-width: 0 40px 30px 0;
    float: right;
    height: 0px;
    width: 0px;
    -webkit-transform: rotate(360deg);
    -moz-transform: rotate(360deg);
    -o-transform: rotate(360deg);
    transform: rotate(360deg);
    z-index: 10;
    border-color: rgba(255, 255, 255, 0) #ffac00 rgba(255, 255, 255, 0) rgba(255, 255, 255, 0);
}
.shape-text {
    color: #252525;
    font-size: 8px;
    font-weight: bold;
    position: relative;
    right: -15px;
    top: 0px;
    white-space: nowrap;
    -ms-transform: rotate(37deg);
    /* IE 9 */
    -o-transform: rotate(360deg);
    /* Opera 10.5 */
    -webkit-transform: rotate(37deg);
    /* Safari and Chrome */
    transform: rotate(37deg);
}
/*FIM BADGES*/
