css:
@-moz-document domain("perspective-daily.de") {
body {
	color: #DDD;
}

div#wrapper {
	background: #292929;
}

.card .text {
	background: #111;
	color: #DDD;
}

.footer_container {
	background: #111;
	border-color: #DDD !important;
}

.navigation {
	background: #111;
}

section.filter span {
	color: #DDD;
}

.ng-binding {
	color: #DDD;
}

.feed section.audio {
	background: #111;
}

#article .read_header a:not(.images) {
	color: #DDD;
}

body #bg {
	background: #292929;
}

#article .content.article .discussions .panel .body .contribution > .text::after {
	background: linear-gradient(rgba(255, 255, 255, 0), #111);
}

div.header,
figure.info_box,
.tabs.image_layout li.active,
.header.image_header .header-container .tabs li.active,
.tabs.image_layout div.tab.active,
.header.image_header .header-container .tabs div.tab.active,
div.fond {
	background: #111 !important;
	color: #DDD !important;
}

div.status span {
	color: #292929 !important;
}

ul.slick-dots li button::before {
	color: #DDD;
}

ul.slick-dots li.slick-active button::before {
	color: #DDD;
}

a.nofb {
	color: #DDD !important;
}

a.nofb div {
	background: #111 !important;
	color: #DDD !important;
}

a.nofb div span {
	color: #DDD !important;
}

figure.diagramm h2,
span.legendText {
	color: #292929 !important;
}

section.what {
	color: #111;
}

.list.default .article > .body {
	background: #111 !important;
}

.list .article > .body .teaser {
	color: #DDD !important;
}

section.read_header div.author {
	background: #29292900 !important;
}

div.list {
	background: #292929;
}

div.author {
	background: #111 !important;
}

div.index {
	background: #111;
}

div.header.guestauthor {
	background: #292929 !important;
	color: #DDD !important;
}

div.header.guestauthor * {
	background: #292929 !important;
	color: #DDD !important;
}

ul.links li * {
	color: #DDD !important;
}

ul.links li *:hover {
	color: #ff6e32 !important;
}

p.pre-copy {
	color: #DDD !important;
}

div.body {
	background: #292929 !important;
}

div.controls * {
	color: #DDD !important;
	border-color: #DDD !important;
}

button#step-0:enabled {
	color: #DDD !important;
	border-color: #DDD !important;
	background: rgba(0, 210, 115, 0.7) !important;
}

button#step-0:enabled:hover {
	color: #DDD !important;
	border-color: #DDD !important;
	background: rgba(0, 210, 115, 0.5) !important;
}

div#search_container {
	background: #101010DD !important;
	color: #DDD !important;
}

input#lookup {
	background: #101010DD !important;
	color: #DDD !important;
}

div.beta-disclaimer {
	color: #00C4FF !important;
}

div.beta-disclaimer a {
	color: #ff6e32 !important;
}

div#search_container div {
	color: #DDD !important;
	border-color: #DDD !important;
}

span.trigger,
span.more {
	color: #DDD !important;
}

div.submenu {
	background: #111 !important;
}

div.submenu .blocks * {
	background: #00000000 !important;
	color: #DDD !important;
}

div.submenu .info {
	background: #005020 !important;
}

div.tiles a span {
	color: #DDD !important;
}
}
