css:
@-moz-document domain("www.satchelone.com") {
#page-content-wrapper .main-content {
    clear: both;
    padding-top: 28px;
    padding-bottom: 28px;
    min-height: 500px;
    min-height: calc(100vh - 50px - 95px);
    border-left: 1px solid #333;
    background: #333;
}

.container-fluid, .only-icon {
    padding-left: 28px;
    margin-left: 0;
    margin-right: 0;
    padding-right: 28px;
}

body {
    color: #ffffff;
}

.satchel-top-bar {
    height: 61px;
    border-bottom: 1px solid #333;
    line-height: 1;
    background-color: #222;
    position: fixed;
    width: 100%;
    top: 0;
    left: 0;
    z-index: 531;
}

.satchel-top-bar__content {
    height: 100%;
    margin-left: 240px;
    -webkit-transition: margin-left .4s;
    transition: margin-left .4s;
}

.__segmented-todos__6a33a .todo-divider:after {
    padding-top: 5px;
    padding-bottom: 5px;
    border: 1px solid #333;
}

.ember-power-select-dropdown {
    color: #fff;
    border-top: none;
    z-index: 1002;
}

.ember-power-select-dropdown {
    border-left: 1px solid #222;
    border-right: 1px solid #ccc;
    line-height: 1.75;
    border-radius: 4px;
    box-shadow: none;
    overflow: hidden;
}

.__todo-item__b724d .date {
    font-weight: 600;
    color: #ccc;
    line-height: 32px;
    font-size: 13px;
    text-align: center;
}

a {
    color: #ccc;
    text-decoration: none;
}

.hr[title]:after, hr[title]:after {
    content: attr(title);
    background-color: #222;
    padding: 6px 35px;
    position: relative;
    top: -10px;
    color: #f6f7fb;
    border-radius: 6px;
}

.form-control {
    width: 100%;
    height: 32px;
    padding: 6px 12px;
    background-color: #333;
    border: 1px solid #f6f7fb;
    border-radius: 4px;
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    -webkit-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}

.ember-basic-dropdown-content {
    position: absolute;
    width: auto;
    z-index: 1000;
    background-color: #222;
}

.__main-header__a7ee6 {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    height: 60px;
    padding: 0 20px;
    background-color: #333;
    border-bottom: 1px solid #333;
    border-left: #222;
    border-right: #222;
}

.__main-header__a7ee6 h1 {
    display: inline-block;
    margin: 0;
    font-size: 20px;
    font-weight: 400;
    color: #fff;
    -ms-flex-negative: 0;
    flex-shrink: 0;
}

.tab-container.tabs-extended {
    background: #333;
    border-left: none;
    border-bottom: 1px solid #333;
    height: 60px;
    padding: 0 25px;
}

.tab-container ul.tabs li a.active, .tab-container ul.tabs li a:focus, .tab-container ul.tabs li a:hover {
    background-color: #333;
    text-decoration: none;
}

.tab-container.tabs-extended ul.tabs li a {
    height: 60px;
    font-weight: 600;
    color: #f6f7fb;
    line-height: 60px;
    min-width: 0;
    padding: 0;
    border-bottom: 4px solid transparent;
}

.satchel-color-bar {
    position: fixed;
    top: 0;
    height: 4px;
    width: 100%;
    /* background: -webkit-linear-gradient(left,#222,#222 12%,#222 13%,#967adc 24%,#4fc1e9 25%,#4fc1e9 36%,#8cc152 36%,#8cc152 48%,#ffce54 49%,#ffce54 60%,#f3a100 61%,#f3a100 72%,#fc6e51 73%,#fc6e51 84%,#da4453 85%,#da4453 100%) 50% 100% no-repeat #f7f9fa; */
    background: linear-gradient(to right,#222,#ccc 12%,#ccc 13%,#ccc 24%,#ccc 25%,#ccc 36%,#ccc 36%,#222 48%,#222 49%,#222 60%,#222 61%,#222 72%,#222 73%,#222 84%,#222 85%,#222 100%) 50% 100% no-repeat #2e414a;
    z-index: 532;
}

.breadcrumb a:not(.brand-primary-text) {
    color: #fff;
}

.breadcrumb {
    padding: 0;
    background-color: transparent;
    margin-bottom: 12px;
    color: #ffffff;
}

.satchel-top-bar .ember-basic-dropdown-trigger {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    padding: 0;
    width: 57px;
    height: inherit;
    background: 0 0;
    border-left: 0px solid #e6e9ed!important;
    border-radius: 0;
    font-size: 14px;
    color: #656d78;
    box-shadow: none!important;
}

.event-dropdown .ember-basic-dropdown-trigger {
    font-size: 26px;
    color: #aab2bd;
}

.satchel-menu__brand {
    FONT-VARIANT: JIS04;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    width: 240px;
    height: 60px;
    padding: 20px;
    background-color: #222;
    border-bottom: 0px solid rgba(255,255,255,.1);
    float: left;
    overflow: hidden;
    -webkit-transition: width .4s,padding .4s;
    transition: width .4s,padding .4s;
}

.satchel-menu {
    background-color: #222;
    color: #000;
    height: 100%;
    min-height: 100%;
    width: 240px;
    margin-top: 60px;
    padding-bottom: 60px;
    position: fixed;
    z-index: 550;
    -webkit-transition: width .4s;
    transition: width .4s;
}

.sidebar-menu__item {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    background-color: #222;
    color: #ffffff;
    font-weight: 600;
    width: 100%;
    height: 48px;
    padding-left: 20px;
    padding-right: 20px;
    border-left: 3px solid #e8000000;
    cursor: pointer;
    border-right-color: #e23d00;
}

.btn-success.active, .btn-success.focus, .btn-success:active, .btn-success:focus, .btn-success:hover, .open>.btn-success.dropdown-toggle {
    color: #fff;
    background-color: #222;
    border-color: #999;
}

.satchel-top-bar .global-search-box .global-search-input {
    height: 40px;
    width: inherit;
    padding: 15px;
    font-size: 14px;
    color: #ffffff;
    background: #353535;
}

.__global-search-box__638d8 input {
    background: #fff;
    border-radius: 4px;
    padding: 0 25px 0 10px;
    width: 260px;
    font-size: 13px;
    border: none;
    height: 30px;
    color: #000;
}

.event-row {
    background-color: #fff;
    padding: 20px;
    border-left: 1px solid #e6e9ed;
    border-right: 1px solid #e6e9ed;
    border-bottom: 1px solid #e6e9ed;
}

.__event__dropdown-menu__28494 .arrow::before {
    border-color: transparent transparent #fff;
    bottom: -1px;
    z-index: 2;
}

.__todo-item__b724d.todo-item-homework {
    border-left: 3px solid #f6f7fb;
}

.__todo-item__b724d {
    width: 100%;
    padding: 15px 25px;
    border: 1px solid #222;
    margin-bottom: 20px;
}

.homework-information {
    background-color: #333;
    border-radius: 4px;
    margin-top: 20px;
}

.__assignment-actions__bca89 .smhw-dropdown-btn {
    background-color: #333;
    position: relative;
    z-index: 100;
    padding: 10px;
}

.__attachment-link__42355 .attachment .file-type {
    text-transform: uppercase;
    text-align: center;
    float: left;
    width: 80px;
    padding: 10px;
    font-size: 20px;
    font-weight: 700;
    border: 1px solid #ccc;
    background-color: #333;
    border-radius: 5px;
    margin-right: 10px;
    min-height: 48px;
}

.__attachment-link__42355 .attachment .filename {
    line-height: 22px;
    color: #f6f7fb;
}

.__attachment-link__42355 .attachment a {
    line-height: 22px;
    color: #0e79c9;
    margin-right: 8px;
}

.img-thumbnail, body {
    background-color: #333;
}

.__segmented-todos__6a33a .todo-divider {
    margin-top: 35px;
    margin-bottom: 35px;
    border-color: #222;
}
}
