css:
@-moz-document url-prefix("https://smartprogress.do/") {
/*    ┌───────────────────────────┐    */
/*    │   Smartprogress.do dark   │    */
/*    │                           │    */
/*    │         By levovix        │    */
/*    └───────────────────────────┘    */

:root {
    --dt-black:  #202020;
    --d-black:   #1d1d1d;
    --l-black:   #232323;
    --dt-white:  #f2f2f2;
    --d-white:   #c5c5c5;

    --l-purple:  #9490EB;
    --dt-purple: #7F7AEB;
    --d-purple:  #524CBD;
    --dt-red:    #ff6555;
    --dt-lime:   #00FF5D;
    --dt-orange: #FF7F3F;
    --l-orange:  #FFA274;
}



/*    ┌───────────────────────────┐
      │        Тело сайта         │     
      └───────────────────────────┘    */

/****         Задний фон         ****/

.content, .page__body-wrapper { background: var(--dt-black); color: var(--d-white); }
.widget { 
    background: var(--l-black);
    box-shadow: 1px 2px 2px var(--d-black);
    border-radius: 10px; 
}
.overlay {
    background: rgb(32, 32, 32, 0.8);
}
/****            Текст            ****/
.page__body, .goal__title-inner, .user-widget__link, .dark-link, ._2o98Ez9ac3, .plan__description {
    color: var(--dt-white);
}
.plan__title, .plan__list {
    color: var(--l-purple);
}
.user-widget__link:hover, .dark-link:hover, ._2o98Ez9ac3:hover {
    color: var(--l-purple);  transition: 0.2s;
}
.user-widget__link, .dark-link, ._2o98Ez9ac3 {
    color: var(--dt-white);  transition: 0.3s;
}
a {text-decoration: none;}
.formatted-content a, ._3tgkT2ikvv {color: var(--l-purple); transition: 0.3s;}
.formatted-content a:hover, ._3tgkT2ikvv:hover {color: var(--d-purple); transition: 0.3s;}

/****          Картинки          ****/
._35DwF3WcTx:hover > img { transition: 0.4s; width: 125%; height: 125%; }
._35DwF3WcTx > img { transition: 0.4s; width: 100%; max-width: 1280px !important; height: 100%; max-height: 720px !important; }
.full-gallery {background: transparent;} /*Задний фон загруз картинок*/

/****        Нижняя часть         ****/
._3hXhP24Ce9 {
    background: var(--dt-black);
	color: var(--l-orange);
    text-decoration: none;
}
.footer {
    background: var(--d-black);
}

/****        Панель справа        ****/
.card__descr--cut:before {
	background-image: linear-gradient(rgba(255,255,255,0), var(--l-black));
}
._2d5HZCVrao, .notice {display: none;}
.user-widget {
    left: 0%;
    margin: 0px;
    margin-top: -120px;
}
.content-cols.widget .content-cols__content {
    width: 100%;
    margin-top: 100px;
}
.content-cols.widget .content-cols__sidebar {display: none;}
.post-sort {border: none; background: var(--l-black); border-radius: 10px;}

/****            Кнопки           ****/
/*На правой панели*/
._3YKGet8_aF ._3PlcAPllhp, .select-chosen--white ._3PlcAPllhp {background: var(--dt-black);}
/************************************/

/*    ┌───────────────────────────┐
      │     Полоса прогресса      │     
      └───────────────────────────┘    */
.grey-frame {
    background-color: var(--dt-black);
    border-bottom: 0px solid transparent;
}
.progressbar-scale__item--complete {
    background: var(--l-purple);
    box-shadow: inset 0 0 0 1px rgba(15,15,15,.05), inset 0 1px 0 rgba(15,15,15,.1), inset 0 1px 1px rgba(15,15,15,.1);
}
.progressbar-scale__item--complete:not(.st-no-hover):hover {background: var(--l-purple);}
.progressbar-scale__item {
    border: 1px solid var(--d-purple);
    background-repeat: none;
    background-image: none;
}
.progressbar-scale__line:first-child .progressbar-scale__pin {
    background-image: none;
}
.progressbar-scale__number {
    color: transparent;
}
.progressbar-scale-tooltip {
    background: var(--l-black);
    border-radius: 10px;
}
/************************************/

/*    ┌───────────────────────────┐
      │           Посты           │     
      └───────────────────────────┘    */
/*Редактор записи дневника*/
.page__body .redactor_editor {background: var(--dt-black);}

._3UCEaUcP_b {background: var(--l-black);} /*изображения*/
._2oUjNAxlou, ._3yHbNGC-vy, .jQ3greL7Hf
{background: var(--l-black);} /*фон*/
/*Кнопки*/
.btn_theme_blue {
  	transition: 0.3s;
    color: var(--dt-white)!important;
    border: none;
    background-color: var(--d-purple);
    background-image: none;
    box-shadow: none;
}
.btn_theme_blue:hover {
  	transition: 0.3s;
    background: var(--l-purple);
    border: none;
}
.btn_theme_gray {
  	transition: 0.3s;
    background: var(--dt-black);
    border: none;
    box-shadow: none;
}
.btn_theme_gray:hover {
  	transition: 0.3s;
    background: var(--d-purple);
}
/*Индикатор дня*/
.e9JgUMmaiQ {background: var(--d-purple); border-radius: 10px; width: 80px; height: 50px;}
.e9JgUMmaiQ strong {padding-top: 5px;}
/************************************/

/*    ┌───────────────────────────┐
      │        Комментарии        │     
      └───────────────────────────┘    */

/****   Написание комментариев   ****/
.form__inp, .UfDQYIR84n {
	background: var(--dt-black);
    color: var(--dt-white);
    border: 1px solid var(--d-black);
    box-shadow: 1px 1px 1px rgba(0,0,0,.2) inset;
  	transition: 0.2s;
}
.form__inp:hover, .UfDQYIR84n:hover {
    border-color: var(--dt-purple);
    box-shadow: 1px 1px 1px rgba(0,0,0,.2) inset;
  	transition: 0.3s;
}
/*Дневник*/
._2oUjNAxlou {border-radius: 10px;}
.redactor_box > div {min-height: 200px !important;}

/****    Полосы у комментариев   ****/
.card__title {color: var(--dt-white);}
._17geJQvfrP+._17geJQvfrP:before {
    color: var(--dt-black);
    opacity: 1;
}
._2m42w5NCr_, ._35sPqegJj9 {border-top-color: var(--d-black);}
._12Wj9yh79h+._12Wj9yh79h:before {border-top-color: var(--d-black); opacity: 1;}
/*Показать больше*/
._2_Hh0pOdWV {
  	transition: 0.3s;
    background: var(--dt-black);
    border-radius: 10px;
    color: var(--d-rhite);
}
._2_Hh0pOdWV:hover {
  	transition: 0.3s;
    background: var(--d-purple);
}
/************************************/


/*    ┌───────────────────────────┐
      │            цель           │     
      └───────────────────────────┘    */

.goal__title-inner 	{color: var(--dt-white);} /*Заголовок цели*/

.plan__title 		{color: var(--dt-orange);} /*Заголовок задачи*/
.plan__description 	{color: var(--d-white);}  /*описание задачи*/
.plan__item--completed .plan__title,
.plan__item--was-complete .plan__title,		  /*Выполненная задача*/ 
.plan__step-completed .plan__title
{color: var(--dt-purple) !important;}

/****            Кнопки           ****/
/*Лайк*/
.btn_theme_gray.st-voted, .btn_theme_gray:disabled{ 
    background: var(--dt-black);
    border: none;
}
/*маленькие*/
.btn_theme_gray .btn__cntr {border: none;}
.btn_tiny .btn__cntr+span, .btn_theme_gray {color: var(--dt-white) !important;}
/*снизу*/
.btn:before {display: none;}
.edit-panel {
    border: none;
    background: var(--l-black);
}
/*ползунок в цели*/
.tumbler {
    background: var(--nav-black);
    box-shadow: 0 1px 0 var(--deadly-black), inset 0 1px 1px var(--post-black);
}
.tumbler__button {
    background: var(--dt-black);
    background-image: linear-gradient(var(--l-purple),var(--d-purple)) !important;
    box-shadow: 0 0 0 1px rgba(127,122,235,.23) inset, 0 1px 0 0 rgba(82,76,189,.68) inset, 0 1px 3px 0 rgba(14,16,16,.47) !important;
}
/*подписчики*/
.edit-panel--compact {background: var(--l-black); border: none;}
/*показать всё*/
.goal__show-full {
  	transition: 0.3s;
    background: var(--dt-black);
    color: var(--dt-white);
    border: none;
    border-radius: 10px;
}
.goal__show-full:hover {
  	transition: 0.3s;
    background: var(--d-purple);
    border: none;
}
/****            Тени             ****/
.goal__hidden-for-subscribers .goal__descr--short:before {
    background-image: none;
}
/*************************************/

/*    ┌───────────────────────────┐
      │       Редактор цели       │     
      └───────────────────────────┘    */

/*УБРАТЬ КАКУ!*/
.form__col--side {display: none;}
.form__col--main {width: 100%;}
/*Фон*/
.form__col--main, ._2qtqtW3-m6, .form__pane, ._2CMabzzKHQ {background: var(--l-black);}
/*Текст*/
.formatted-content p {color: var(--d-white);}
.page__body .redactor_editor h3 {color: var(--dt-white);}
.redactor_editor a {color: var(--l-purple) !important; text-decoration: none !important; transition: 0.3s;}
.redactor_editor a:hover {color: var(--d-purple) !important; transition: 0.3s;}
/*Редактор текста*/
.redactor_box .redactor_toolbar {
    background: #292929;
    border: none;
    box-shadow: none;
    border-radius: 10px;
}
.page__body .redactor_box {
    background: var(--dt-black);
    border-radius: 10px;
}
.page__body .redactor_editor {
    border: none;
}
.redactor_box {border: none;}
/*Кнопки*/
._1YKHzsutd6, .pYxfdMHCFQ {
    background: var(--dt-black); 
    color: var(--dt-white); 
    box-shadow: 2px 2px 2px rgba(0,0,0,.3) inset;
    border-radius: 8px;
}
._1YKHzsutd6:last-child .pYxfdMHCFQ {
    border-radius: 0 8px 8px 0;
}
._1YKHzsutd6:first-child .pYxfdMHCFQ{
    border-radius: 8px 0 0 8px;
}
._1OPQK5NjT1 ._3AmXmqa2Gb:checked+.pYxfdMHCFQ {
    background: var(--d-purple); 
    color: var(--dt-white); 
    box-shadow: none;
}
/*Кнопки-переключатели*/
._3PlcAPllhp {
    transition: 0.3s;
    color: var(--dt-white);
    background: var(--dt-black);
    border: 1px solid var(--dt-black);
}
._3PlcAPllhp:hover {
    transition: 0.3s;
    color: var(--dt-white);
    background: var(--dt-black);
    border-color: var(--dt-purple);
}
.H7VY_88Aon {color: var(--d-purple); transition: 0.3s;}
._3PlcAPllhp:hover > .H7VY_88Aon {color: var(--dt-purple); transition: 0.3s;}
select {
    color: var(--dt-white);
    background: var(--dt-black);
}
/*?*/
._3IZaM62nk6 {background: transparent; color: var(--d-white);}
/*************************************/



/*    ┌───────────────────────────┐
      │         Сообщения         │     
      └───────────────────────────┘    */


/****      Задний фон сообщений     ****/
._3EMqlucvYR, ._3EMqlucvYR:before {
	background: var(--l-black); 
    color: var(--dt-white);
}
._3EMqlucvYR:before {
    border-right-color: var(--l-black);
    border-top-color: var(--l-black);
}
._2iyq02A5vc ._3EMqlucvYR {background-color: #2ab8d5 !important;}
._2ycnCy8gnF > div {
	background: var(--d-purple) !important; 
    color: var(--dt-white);
}
._2ycnCy8gnF > div:before {
    border-left-color: var(--d-purple) !important;
    border-top-color: var(--d-purple) !important;
}
/****        Панель диалогов        ****/
._10tHf3Q1Gy:first-child, ._2BpRGw8aHE, ._3BKTuXUgmE{
    background: var(--l-black);
}
._3BKTuXUgmE:before {background: var(--dt-black);}
._13kOr6-3_a, ._10AM0dTunC, ._10tHf3Q1Gy:first-child ._3BKTuXUgmE {border: none;}
._10AM0dTunC {background: var(--dt-purple);}
/************************************/
}
