@-moz-document url-prefix("https://www.mercadolibre") {
.ui-item__content {
    position: relative;
    border-top: 1px solid rgba(51,51,51,.1);
    padding: 24px 24px 14px;
    background: #232323;
}
.payment-data .payment-data-section {
    -webkit-box-flex: 1;
    -ms-flex: 1 0 auto;
    flex: 1 0 auto;
    font-size: 18px;
    padding: 20px;
    white-space: nowrap;
    background: #232323;
    height: 90px;
}
.payment-data .payment-data-title {
    display: inline-block;
    margin-top: 1px;
    color: white;
}
.price-tag.ui-item__price .price-tag-fraction, .price-tag.ui-item__price .price-tag-symbol {
    height: 24px;
    display: -webkit-inline-box;
    display: -ms-inline-flexbox;
    display: inline-flex;
    -webkit-box-align: end;
    -ms-flex-align: end;
    align-items: flex-end;
    color: white;
}
.ui-item:hover .ui-item__action, .ui-item:hover .ui-item__discount-price, .ui-item:hover .ui-item__title {
    opacity: 1;
    visibility: visible;
    -webkit-transition: visibility .1s .1s;
    -o-transition: visibility .1s .1s;
    transition: visibility .1s .1s;
    color: white;
}
.section-header h1 {
    color: #fff;
}
.ui-item *, .ui-item :after, .ui-item :before {
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    color: white;
}
.nav-bounds.nav-bounds-with-cp .nav-menu-cp.nav-menu-cp-logged .nav-menu-cp-send {
    position: relative;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    top: 1px;
    font-size: 12px;
    color: rgb(255, 255, 255);
    display: block;
    width: 100%;
    left: 8px;
    padding: 6px 0 0 13px;
    margin-right: 8px;
    line-height: 10px;
    height: 20px;
}
.nav-bounds.nav-bounds-with-cp .nav-menu-cp .nav-menu-link-cp {
    color: #0ff;
    padding-left: 18px;
    padding-right: 3px;
    font-size: 14px;
    width: 100%;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    display: inline-block;
}
main {
    background-color: #151515;
    font-family: Proxima Nova,-apple-system,Helvetica Neue,Helvetica,Roboto,Arial,sans-serif;
    -webkit-font-smoothing: antialiased;
}
.nav-header {
    background-color: #232323;
    border: 0;
    position: relative;
}
.nav-menu-item a, .nav-menu-item a:link, .nav-menu-item a:visited {
    color: rgb(255, 255, 255);
}
input[type=text].nav-search-input, input[type=search].nav-search-input {
    padding: 7px 60px 9px 15px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
    background-color: #3e3e3e;
    border: 0 rgba(0,0,0,.2);
    color: white;
}

}

