css:
@-moz-document domain("innri.lsh.is") {
body {
    background: #333;
}
.full-width-white-background.bigBorderExtra, .full-width-grey-background.bigBorderExtra {
    background: #333;
    color: #eee;
    border-bottom: 1px solid #444;
}
.full-width-white-background, .full-width-grey-background, .helpful {
    background: #444;
    color: #eee;
}
#footer, .helpful {
    border-top: 1px solid #666;
}
.title_with-line, .frontpage_news-time {
    color: #eee;
}
.tab-content .tab-pane {
    background: rgba(255,255,255,0.1);
    border: 1px solid rgba(255,255,255,0.1);
}
.content-nav {
    border: none;
}
.content-nav .lsh-tabs li,
.content-nav .lsh-tabs li:last-child{
    background: rgba(255,255,255,0.2);
    border: none;
}
.content-nav .lsh-tabs li.active {
    background: rgba(255,255,255,0.3);
    border: none;
}
.frontpageEvents__list_item, .frontpage_news-list-item {
    border: none !important;
}
.frontpage_news-first {
    border-color: rgba(255,255,255,0.2) !important;
}
body *, body a, .frontpage_news-first-title, .frontpage_news-list-title, .frontpageEvents__list_item-small {
    color: #eee !important;
}
.direct .direct-list-item-wrapper ul.direct-list li, .main-nav .h3 {
    border: none;
}
.backgroundImg::after {
    background: #333;
}
.info-banner {
    background: rgba(255,255,255,0.3);
    border: none;
}
.search-form .form-control {
    background: rgba(255,255,255,0.1);
    color: #eee !important;
    border: 1px solid rgba(255,255,255,0.1);
}
.search-form .form-control ::placeholder {
    color: #eee !important;
}
.lisa-search-autocomplete,
.education-page #infoquery {
    background: #444;
    border: 1px solid rgba(255,255,255,0.2);
    box-shadow: none;
}
.lisa-search-autocomplete ul li.staff-item {
    background: rgba(255,255,255,0.1);
    border: none;
}
.user-card {
    background: rgba(255,255,255,0.1) !important;
}
.lisa-search-autocomplete ul li {
    border: none;
}
.lisa-search-autocomplete>div {
    background: rgba(255,255,255,0.1);
    border: none;
}
.drop {
    background: #333 !important;
}
.content .breadcrumb>li, .breadcrumb>li {
    background: none;
}
.content .breadcrumb>li>a:after, .breadcrumb>li>a:after, .content .breadcrumb>li>a:before, .breadcrumb>li>a:before {
    border: none;
}
.custom-buttons .button-wrapper .custom-checkbox,
.memo .memotext,
button.add, .alert-danger {
    background: #333;
    color: #eee;
}
.my-phone-numbers .myPhonebook .myDataHolder li, .my-phone-numbers .myExtPhonebook .myDataHolder li, .my-phone-numbers .myPhonebook #lsh-contacts li, .my-phone-numbers .myExtPhonebook #lsh-contacts li, .myPhonebook, .search-item-wrapper {
    border: none;
    background: rgba(255,255,255,0.1) !important;
}

input[type=text], select, textarea {
    background: rgba(255,255,255,0.1) !important;
}
.table tr:nth-child(even){
    background: rgba(255,255,255,0.1);
}
.table-striped>tbody>tr:nth-of-type(odd) {
    background: rgba(255,255,255,0.2);
}
table.phonebook tr {
    background: rgba(255,255,255,0);
    border-top: 1px solid rgba(255,255,255,0.1);
}
.search-wrapper .button-wrapper .disabled {
    background: rgba(255,255,255,0.2);
}
.table thead tr {
    background: #333;
}
.header-bottom {
    background: #222;
}
.header-top {
    background: #111;
}
.search-form .search-button-group a, .search-form .search-button-group button {
    margin-top: 10px;
    background: #111;
}
.search-form .search-button-group {
    margin-right: 0px;
}
.mySites {
    background: none;
}
}
