css:
@-moz-document url-prefix("https://bitcointalk.org"){
body {
    background-color: #fff9bd;
    padding: 12px 30px 4px 30px;
    
    
}

table#smfheader {
    margin-top: 20px;
}

.catbg, tr.catbg td, .catbg3, tr.catbg3 td {
    background: none;
    color: #ffffff;
    padding-left: 10px;
    padding-right: 10px;
    background-color: #ff9400;
}

.tborder {
    padding: 0;
    border: none;
    background-color: #FFFFFF;
    border-radius: 10px;
}



td#toppages {
    background-color: #ffe600;
    color: black;
    border-radius: 7px;
    padding-left:10px;
    padding-right:5px;
    border:solid 1px red;
}

div#bodyarea .maintab_back, div#bodyarea .mirrortab_back {
    background: #ffe600;
    border-radius: 7px;
    color: #cd7474
}

div#bodyarea .maintab_back a:link, div#bodyarea .maintab_back a:visited, div#bodyarea .mirrortab_back a:link, div#bodyarea .mirrortab_back a:visited {
    color: #7a4301;
    text-decoration: none;
}

.bordercolor {
    margin-top: 10px;
}

.titlebg, tr.titlebg th, tr.titlebg td, .titlebg2, tr.titlebg2 th, tr.titlebg2 td {
    color: black;
    font-style: normal;
    background: none;
    border-bottom: none;
    border-top: solid 1px #FFFFFF;
    padding-left: 10px;
    padding-right: 10px;
    background-color:#eacc17;
}

.windowbg2 {
    color: #000000;
    background-color: #f1cf85;
    padding: 0px;
}

.windowbg, #preview_body {
    color: #533423;
    background-color: #f1cf85;
    padding: 0px;
}

#bodyarea > form > table > tbody > tr > td > table {
    margin-top: 15px;
}

.maintab_first {
    background: #f7931a;
    width: 10px;
    border-radius: 0px 0px 0px 7px;
}

.maintab_back {
    background: #f7931a;
}

.maintab_last {
    background: #f7931a;
    width: 8px;
    border-radius: 0px 0px 7px 0px;
}

.maintab_active_back {
    background: #976c0d;
}

.maintab_active_last {
    background: #976c0d;
    width: 8px;
}

.maintab_active_first {
    background: #976c0d;
    width: 6px;
}

.windowbg3 {
    color: #85571e;
    background-color: #f4af5d;
}

.catbg, .catbg2, .catbg3 {
    border-bottom: none;
}

td.td_headerandpost {
    background-color: #fffce2;
}

.bordercolor {
    margin-top: 10px;
    background: none;
}

.post {
    font-size: 13px;
    padding: 3%;
}

.post, .personalmessage {
    width: 94%;
    overflow: auto;
    line-height: 1.3em;
}

td.middletext {
   color:#7a0f0f;
}

a.navPages {
    color: white;
}

.nav, .nav:link, .nav:visited {
    color: #aa5d00;
    text-decoration: none;
}

.subject {
    display: none;
}

a:visited {
    color: #535a60;
    text-decoration: none;
}
a:link {
    color: #7a4301;
    text-decoration: none;
}

.catbg2, tr.catbg2 td {
    background: #f7931a;
    color: #c41616;
    padding-left: 10px;
    padding-right: 10px;
}

.quote {
    color: #000000;
    background-color: #ffe5b9;
    padding: 15px;
    margin: 1px 3px 6px 6px;
    font-size: 11px;
    line-height: 1.4em;
    border: 0px solid #d0d0e0;
    border-left: 5px solid #8d6636;
   
}

.quoteheader, .codeheader {
    color: #476C8E;
    text-decoration: none;
    font-style: normal;
    font-weight: bold;
    font-size: 10px;
    line-height: 1.2em;
    margin-left: 6px;
    margin-top: 5px;
}

.tborder #smfheader td.catbg:first-child {
    border-radius: 7px 0 0 0;
}

.tborder #smfheader td.catbg:last-child {
    border-radius: 0 7px 0 0;
}

.tborder > .catbg:first-child, table.bordercolor tr td.catbg:first-child, .catbg2:first-child {
    border-radius: 7px 7px 0 0;
}

.catbg, tr.catbg td, .catbg3, tr.catbg3 td {
    background: none;
    color: #4b2a00;
    padding-left: 10px;
    padding-right: 10px;
    background-color: #ff9400;
}

.catbg img {
    display: none;
}

input[type="submit"] {
    background-color: #ff8c02;
    color: white;
    border: 1px solid white;
    border-radius: 5px;
    padding: 5px;
}

td.smalltext {
    background-color: #fffce2;
}

#quickModForm .windowbg, #quickModForm .windowbg2 {
    padding: 0;
    border-radius: 10px;
}

td.poster_info {
    padding: 15px;
}
}
