css:
/* i really want this to be global */
@-moz-document domain(nesa-sg.ch) {
#content-card {
    background-color: #252525;
}

body {
    color: white !important;
}

.login-header, .login-footer {
    background-color: rgba(68, 68, 68, 0.8);
}
.sn-menu-icon {
    color: black;
}


h1,
h2,
h3,
h4,
h5,
p,
span,
a,
div,
i {
    color: white;
}
.mdl-data-table.mdl-js-data-table.mdl-table--listtable {
    background-color: #3c3c3c;
}

.mdl-card--information {
    background-color: rgb(175, 195, 114);
}

#sn_mobile_card_content b {
    color: white;
}

.mdl-table--row-dense {
    background-color: #3c3c3c;
}

tr {
    background-color: #3c3c3c;
}

tr:hover {
    background-color: #4c4c4c !important;
}

.mdl-table--listtable tr:hover {
    background-color: #4c4c4c !important;
}

td {
    color: white !important;
}

.mdl-table--listtable th {
    background-color: #282828;
    color: white;
}

.mdl-data-table {
    border-left: 1px solid #3f3f3f;
}

.mdl-data-table td {
    border-top: 1px solid #3f3f3f;
}

.mdl-data-table tbody tr:hover {
    background-color: #8b8b8b;
}

#sn_mobile {
    background-color: #3c3c3c;
}

a.mdl-button {
    color: white;
}

#navigation-list {
    background-color: #252525;
}

.mdl-data-table.mdl-js-data-table.mdl-shadow--2dp.mdl-table--cls-print-table {
    background-color: #3c3c3c;
}

.mdl-card--important {
    background-color: rgb(199, 142, 81);
    color: white;
}

.mdl-card__supporting-text {
    color: white;
}

.far.fa-bars.fa-xs {
    color: black;
}
.mdl-table--row-dense:hover {
    background-color: rgba(0, 0, 0, .16)
}
}
