css:
@-moz-document domain("chan.sankakucomplexxx.com")
{
body, body.en, body.ja {
    padding: 0px;
    margin: 0px;
    background: #222 none repeat scroll 0% 0%;
    min-width: 940px;
    color: silver;
}
div.status-notice {
    border: none;
    padding: 0.5em;
    margin: 0em 1em 0em;
    background: #222 none repeat scroll 0% 0%;
    text-align: center;
}
div#header {
    margin-bottom: 1em;
    background: #222 none repeat scroll 0% 0%;
    overflow: hidden;
    min-width: 940px;
}
div#header ul#subnavbar {
    background-color: #222;
    padding: 0.5em 0em 0 1em;
    margin: 0 5px;
    border: 1px solid #333;
    border-radius: 5px;
    height: 2em;
}
div#news-ticker {
    padding: 5px;
    background: #222 none repeat scroll 0% 0%;
    border-bottom: 2px solid #333;
    overflow: hidden;
    font-size: 1.5em;
}
div.sidebar {
    border: 1px solid #333;
    border-radius: 5px;
}
ul.flat-list {
    background: #222 none repeat scroll 0% 0%;
}
div#header ul#navbar li.current-page {
    background-color: #252525;
    border-width: 1px 1px 2px;
    border-style: solid;
    border-color: #333 #333 #252525;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
}
div#header ul#navbar li:hover > ul {
    display: block;
    position: absolute;
    background: #252525 none repeat scroll 0% 0%;
    height: 3.5em;
    width: 100%;
    font-size: 0.6em;
    padding-top: 0.5em;
    border-bottom: medium none;
    margin-top: 4px;
}
div#header ul#navbar li:hover {
    background-color: #252525;
    border-width: 1px 1px 2px;
    border-style: solid;
    border-color: #555 #555 #252525;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    border-radius: 5px;
}
div#header ul#navbar li {
    font-size: 130%;
    padding: 0px 10px 2px;
    margin: 0px;
    border-width: 1px 1px medium;
    border-style: solid solid none;
    border-color: #333 #333;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    border-radius: 5px;
}
div#pending-notice {
    background: #222;
    border: 1px solid #333;
}
div#parent-preview span.thumb, div#child-preview span.thumb {
    height: auto;
}
div#post-content { padding-top: 0px; }

#popular-preview {
    border: 1px solid #333;
    border-radius: 5px;
    background: none;
}
.popular-preview-post { margin-top: 0.5em; }
.popular-rank { display: none; }

a:link, a:visited { color: #8888BB;}
a:hover { color: #59B4D9; }

.tag-type-artist > a, a.tag-type-artist { color: #A00; }
.tag-type-artist > a:hover { color: #C66666; }
.tag-type-studio > a { color: #FF2DCD; }
.tag-type-studio > a:hover {color: #FF93FF; }
.tag-type-character > a { color: #0A0; }
.tag-type-character > a:hover { color: #66CC66; }
.tag-type-copyright > a { color: #A0A; }
.tag-type-copyright > a:hover { color: #CC66CC; }
.tag-type-medium > a { color: #2585BB; }
.tag-type-medium > a:hover { color: #7CB6D6; }
.tag-type-meta > a { color: #5C00FF; }
.tag-type-meta > a:hover { color: #9D66FF; }
.tag-type-operator a { color: #999; }
.tag-type-operator > a:hover { color: #C2C2C2; }

#autosuggest { border:1px solid #333; border-radius: 3px; }
#autosuggest span:first-child { color:silver; }
#autosuggest li { background: #222; }
#autosuggest li:hover, #autosuggest li#focussedLi { background: #1a1a1a; }
#autosuggest li:hover span, #autosuggest li#focussedLi span { color: white; }
#autosuggest li span:nth-of-type(2) { text-align: right; }

input#tags {
    border: 1px solid #333;
    background: #222 linear-gradient(#111, #222) repeat scroll 0% 0%;
    border-radius: 35px;
    width: 182px;
    height: 20px;
    padding: 2px 8px;
    overflow: hidden;
    color: #fff;
}
input#tags:focus {
    background: #333 none repeat scroll 0% 0%;
    outline: medium none;
    box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.5) inset;
}
input#tags:active {
    background: #333 none repeat scroll 0% 0%;
    outline: medium none;
    box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.5) inset;
}
input#tags:hover {
    background: #333 none repeat scroll 0% 0%;
    outline: medium none;
    box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.5) inset;
}
#recommendations-paginator > span.recommended-prev {
    font-size: 2.5em;
    float: left;
    margin: 0.1em 0.25em 0.1em 45%;
    padding: 2px 6px;
    font-weight: bold;
    border: 1px solid #555;
    color: #888;
}
#recommendations-paginator > span.recommended-next {
    font-size: 2.5em;
    float: left;
    margin: 0.1em 0px 0.1em 0%;
    padding: 2px 6px;
    font-weight: bold;
    border: 1px solid #555;
    color: #888;
}
a:hover {
    color: #aaa;
    text-decoration: none;
}
.no-comments {
    color: #888;
    font-size: 1.4em;
    font-weight: bold;
}

span.thumb { /*[[grid_option]]*/ }

img.preview { border-radius: 7px; }
img.preview:hover { border: 1px dashed white; margin: 1px; }

#add-to-favs, #remove-from-favs { background-image: none; }
.favoriteIcon { margin-left: auto; margin-right: auto; }

#rating {
    width: 180px;
    margin-left: auto;
    margin-right: auto;
}
.unit-rating {
    list-style: outside none none;
    margin: 0px;
    padding: 0px;
    width: 180px;
    height: 34px;
    position: relative;
    background: transparent url("https://i.imgur.com/cJE4atN.png") repeat-x scroll left center;
}
.unit-rating li {
    background: transparent url("https://i.imgur.com/cJE4atN.png") repeat scroll left top;
    height: 34px;
    display: block;
    text-indent: -9000px;
    z-index: 1;
}
.unit-rating li a:hover {
    margin-top: 2px;
    width: 180px;
    background: transparent url("https://i.imgur.com/cJE4atN.png") repeat scroll left bottom;
    z-index: 2;
    left: 0px;
}
.unit-rating li.star-full { width: 36px; margin-top: -1px; }
.unit-rating li.star-half { width: 18px; margin-top: -1px; }
.unit-rating a.r1-unit{ left: 0px; }
.unit-rating a.r1-unit:hover{ width:36px; }
.unit-rating a.r2-unit{ left:36px; }
.unit-rating a.r2-unit:hover{ width: 72px; }
.unit-rating a.r3-unit{ left: 72px; }
.unit-rating a.r3-unit:hover{ width: 108px; }
.unit-rating a.r4-unit{ left: 108px; }
.unit-rating a.r4-unit:hover{ width: 144px; }
.unit-rating a.r5-unit{ left: 144px; }
.unit-rating a.r5-unit:hover{ width: 180px; }

div#headerlogo { display: none; }
div#sp1 { display: none; }
}
