css:
@-moz-document domain("praktomat.cs.kit.edu") {
body {
    color: #ccc;
    background: #333;
}

* {
    scrollbar-color: #444 #333;
}



a {
    color: #aaf;
}



.ui-tabs-anchor {
    background: #444;
}
a:link.ui-tabs-anchor {
    color: #777;
}
a:hover.ui-tabs-anchor {
    color: #aaa;
}
.ui-tabs-active {
    background: #333 !important;
}
.ui-state-active .ui-tabs-anchor {
    background: #333 !important;
}
.ui-state-active a {
    color: #ccc !important;
}
a.ui-tabs-anchor span {
    color: #f88 !important;
}
.ui-tabs-nav {
    background: #8ab;
}
.ui-widget-content {
    color: #ccc;
    background: #333;
}
.ui-widget-content a {
    color: #aaf;
}



.linenodiv {
    color: #777;
}



.o {
    color: #fff;
}
.n {
    color: #ddf;
}
.na {
    color: #aaf;
}
.mi {
    color: #7af;
}
.nc {
    color: #ddf;
}
.kd {
    color: #f92;
}
.k {
    color: #f92;
}
.kt {
    color: #f92;
}
.nf {
    color: #ff5;
}
.c1 {
    color: #5c4;
}
.s {
    color: #5c4;
}



div.removed {
    background: #843 !important;
}
.deletedChar {
    background: #d40 !important;
}
div.added {
    background: #483 !important;
}
.addedChar {
    background: #4c0 !important;
}
a.checkerinline:link {
    color: #ccc;
}
a.checkerinline:visited {
    color: #ccc;
}



.ui-icon-triangle-1-e.ui-icon {
    -webkit-filter: invert(80%);
    filter: invert(80%);
}
.ui-icon-triangle-1-e.icon {
    -webkit-filter: invert(80%);
    filter: invert(80%);
}
.ui-icon-script {
    -webkit-filter: invert(80%);
    filter: invert(80%);
}
.ui-icon-disk {
    -webkit-filter: invert(80%);
    filter: invert(80%);
}



::selection {
    background: #55c;
}
}
