css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("domic.isu.ru") {

body {
  background: 
  linear-gradient(135deg, black 25%, transparent 25%) -50px 0,
  linear-gradient(225deg, black 25%, transparent 25%) -50px 0,
  linear-gradient(315deg, black 25%, transparent 25%),
  linear-gradient(45deg, black 25%, transparent 25%);	
  background-size: 2em 2em;
  background-color: #232323;
 color: #aeaeae;
 font-family: Arial;
}

a{
 text-decoration: none;
 color: rgb(51, 155, 255);
}

#authorization iframe {
   display: none;
}

#authorization input {
   border-radius: 4px;
   width: 200px;
}

#authorization select {
   border-radius: 4px;
   width: 200px;
}

p a{
 text-decoration: none;
 color: rgb(51, 155, 255);
}

body h2{
 display: none;
}

#deadlines div {
  display: none;
}

#authorization {
position: absolute;
border: 2px solid rgb(51, 102, 204);
width: 200px;
left: 8px;
padding: 4px;
background-color: #0f0f0f;
color: #aeaeae;
border-radius: 12px;
}

#authorization small{
   display: none;
}

#content {
position: absolute;
width: 720px;
left: 218px;
border: 2px solid rgb(51, 102, 204);
padding: 4px;
background-color: #0f0f0f;
color: #aeaeae;
border-radius: 12px;
}

#content h3{
   display: flex;
   align-items: center;
   justify-content: center;
   color: #36c;
}


#content h4{
   display: flex;
   align-items: center;
   justify-content: center;
   color: #36c;
}


#content tbody {

}

#content a.done {
color: #339bf1;
}

#navbar {
color: #aeaeae;
font-size: 18px;
padding: inherit;
opacity: 30%;
}

#navbar a{
text-decoration: none;
color: #aeaeae;
}

#study-entity-list a
{
   color: rgb(51, 155, 255);
}

#module-description{
  color: #0f0f0f;
}

.h4 {
 color: #aeaeae;
}

.learning-level-top-border {
  border: 1px solid black;
  font-size: 6px;
  display: block;
}
.learning-level-bottom-border {
  border-bottom: 1px solid gray;
  border-left: 1px solid gray;
  border-right: 1px solid gray;
  border-top: 1px solid gray;
  font-size: 6px;
  display: block;
}
.learning-level-student {
  background-color: rgb(51, 204, 255);
}

.learning-level-student .span {
  background-color: #ee4eaf;
}

.learning-level-min-max {
  border-top: 2px solid black;
  background-color: rgb(255, 255, 153);
}
.learning-level-average {
  background-color: #f00;
}
.study-entity-type-label {
  font-size: xx-small;
}
.student-comment {
  background: #151515;
  color: whitesmoke;
}
.instructor-comment {
  background: #151515;
  color: whitesmoke;
}

.instructor-comment h4{
  color: rgb(64, 164, 255);
}

.alert-success {
  color: #61dd5d;
  background-color: #0f0f0f;
  border-color: #000;
}

table.stjsl-skin-simple tr.stjsl-odd {
  background-color: #191919;
 }

table.stjsl-skin-simple tr.stjsl-even {
  background-color: #191919;
 }

table.stjsl-skin-simple tr.stjsl-even.stjsl-selected,
table.stjsl-skin-simple tbody tr.stjsl-even.hover {
  background-color: #191919;
 }

table.stjsl-skin-simple tr.stjsl-odd.stjsl-selected,
table.stjsl-skin-simple thead th.stjsl-selected,
table.stjsl-skin-simple tbody tr.stjsl-odd.hover {
  background-color: #191919;
 }


}
