css:
@-moz-document url-prefix("http://www.starwars-noj.fr/") {
body {
	background-image: url("https://zupimages.net/up/19/41/khrm.jpg");
	background-size: 100%;
	background-repeat: no-repeat;
	background-attachment: fixed;
}

/* links */

a, a.action::after {
	color: #25AAE5;
	text-decoration: none;
    transition : ease-in-out 0.2s;
}

a:visited {
	color: #25AAE5;
	text-decoration: none;
}

a:active {
	color: white;
	text-decoration: none;
}

a:hover, a.action:hover::after {
	color: white;
	text-decoration: none;
}


/* boxes */

.content-box {
	background-color: rgba(0, 0, 0, 0.8);
	border: 1px solid #25AAE5;
	box-shadow: 0 0 5px #25AAE5;
    border-radius: 0 !important;
}

.span6.mea {
    width: 568px;
	border: 1px solid #25AAE5;
	box-shadow: 0 0 5px #25AAE5;
    border-radius: 0 !important;
}

.table-bordered {
    background-color: rgba(0, 0, 0, 0.8);
    border: none;
}

.table-bordered th {
    border-radius: 0 !important;
    border: none;
}

.table-bordered td {
    border-radius: 0 !important;
    border: none;
}

.mea-play {
	background-image: url('http://getwallpapers.com/wallpaper/full/b/6/d/664907.jpg') !important;
    background-repeat: no-repeat;
    background-size: 100% auto;
    background-position: 0 -55px !important;
}

.mea-visit {
	background-image: url('http://getwallpapers.com/wallpaper/full/1/0/7/664991.jpg') !important;
    background-repeat: no-repeat;
    background-size: 100% auto;
    background-position: 0 -80px !important;
}

.mea-register {
	background-image: url('http://getwallpapers.com/wallpaper/full/e/b/6/663926.jpg') !important;
    background-repeat: no-repeat;
    background-size: 100% auto;
    background-position: 0 -30px !important;
}

.carousel-inner {
	opacity: 0.9;
}



/* Scrollbar */
::-webkit-scrollbar {
    width: 2px;
}
::-webkit-scrollbar-track {
    visibility: hidden;
}
::-webkit-scrollbar-thumb {
    background: #25AAE5;
}
::-webkit-scrollbar-thumb:hover {
    background: #A0A2A1;
}
::-webkit-scrollbar-thumb:active {
    background: white;
}
body {
    overflow-y: scroll;
}


/* navbar */

.navbar-inner {
	border-bottom: 1px solid #25AAE5 !important;
	box-shadow: 0 0 5px #A9EEFF !important;
}

.navbar-inner a, .navbar-inner a i {
	color: #A0A2A1;
	text-decoration: none;
	transition : ease-in-out 0.2s;
}

.navbar-inner a:hover:not(.brand) {
	text-decoration: none;
	color: #A9EEFF !important;
	text-shadow: 0 0 10px #A9EEFF !important;
    border-bottom: 1px solid #A9EEFF !important;
	background-color: rgba(50, 50, 50, 0.68) !important;
}

/* petite navbar */

ul.nav.nav-pills a {
    border-radius: 0;
}

/* petite petite navbar */

.nav-tabs {
	border-bottom: 1px solid #25AAE5 !important;
}

.nav-tabs li a {
	border-radius: 0;
}

/* menu déroulant */

.dropdown-menu {
    border: none;
    border-radius: 0;
    background-color: rgba(0, 0, 0, 0.9);
    margin-top: 10px;
}

.dropdown-menu::before {
    visibility: hidden;
}
.dropdown-menu::after {
    visibility: hidden;
}

.navbar-inner .dropdown-menu a:hover {
    color: white !important;
    border-bottom: none !important;
    text-shadow: none !important;
    background: rgba(50, 50, 50, 0.68) !important;
}

/* champs de texte */

input[type="text"] {
    border-radius: 0 !important;
    border: 1px solid rgba(46,95,128,0.8);
}

input[type="text"]:focus {
    border: 1px solid #25AAE5 !important; 
}

textarea {
    border-radius: 0 !important;
    border: 1px solid rgba(46,95,128,0.8);
}

textarea:focus {
    border: 1px solid #25AAE5 !important;
}

/* bouttons */

.btn, .btn:visited {
	color: white !important;
	background: #25AAE5;
	border: 1px solid white;
    border-radius: 0 !important;
	box-shadow: 0 0 10px #25AAE5;
	transition: ease-in-out 0.2s;
}

.btn:hover, .btn:active, .btn:focus {
	color: #25AAE5 !important;
    background: white !important;
	border: 1px solid #25AAE5;
    background-position: 0;
    transition: ease-in-out 0.2s;
}


/* gros bouttons */

.maxi-buttons {
    display:flex;
	flex-wrap: wrap;
	justify-content: left;
}

.maxi-buttons a {
	color: #25AAE5;
    background-color: rgba(0, 0, 0, 0.7) !important;
	border: 1px solid #000;
    border-radius: 10px !important;
	text-shadow: 0px 0px 10px #25AAE5;
    opacity: 1 !important;
	transition : ease-in-out 0.2s;
}

.maxi-buttons a span {
	border-radius: 10px !important;
}

.maxi-buttons a:hover {
	color: #A9EEFF;
	text-shadow: 0px 0px 10px #A9EEFF;
	box-shadow: 0px 0px 5px #A9EEFF;
	border: 1px solid #A9EEFF;
}

.maxi-buttons .conseil span {
    background-image: url('https://zupimages.net/up/19/48/20o1.jpg') !important;
    background-position: 0 !important;
}

.maxi-buttons .jedisquadron span {
    background-image: url('https://zupimages.net/up/19/48/ogan.jpg') !important;
    background-position: 0 !important;
}

.maxi-buttons .pnj span {
    background-image: url('https://zupimages.net/up/19/48/npxu.jpg') !important;
    background-position: 0 !important;
}

.maxi-buttons .membres span {
    background-position: -1470px -95px !important;
}

.maxi-buttons .regles span {
    background-position: -420px 0 !important;
}

.maxi-buttons .archives span {
    background-position: 0 -95px !important;
}

.maxi-buttons .pouvoirs span {
    background-position: 0 -190px !important;
}

.maxi-buttons a[href="/archives-jedi/statistiques"] span {
    background-position: -840px 0 !important;
}

.maxi-buttons a[href="/archives-jedi/chambres"] span {
    background-position: -210px 0 !important;
}

.maxi-buttons a[href="/archives-jedi/plans"] span {
    background-position: -1050px -190px !important;
}

.maxi-buttons .voie-erudit span {
    background-image: url('https://zupimages.net/up/19/48/hvwq.jpg') !important;
    background-position: 0 !important;
}

.maxi-buttons .voie-maitre-d-armes span {
    background-image: url('https://zupimages.net/up/19/48/ifea.jpg') !important;
    background-position: 0 !important;
}

/* avatars */

.media-object.pull-left.avatar.small {
	box-shadow: 0px 0px 5px #25AAE5;
    transition : ease-in-out 0.2s;
}

img.avatar, img.pull-left {
	box-shadow: 0 0 5px #25AAE5 !important;
    border-radius: 10% !important;
}

a img.avatar {
    transition : ease-in-out 0.2s;
}

a:hover img.avatar {
	box-shadow: 0 0 10px #A9EEFF !important;
}


/* inventaire */

.object-list > .hide-on-delete, .object-list > .hide-on-delete > .popovered > img {
    border-radius: 10%;
    transition : ease-in-out 0.2s;
}

.object-list > .hide-on-delete:hover {
    background-color: rgba(255, 255, 255, 0.2);
}


/* compétences */

.media .first, .media .last {
	border-radius: 0 !important;
}

.media .popovered {
	border-radius: 0 !important;
    text-shadow: none;
}

.media .popovered:hover {
	border-radius: 0 !important;
    background-color: white !important;
    color: #3c3c3c;
}


/* citations */

.citation {
	border-radius: 0 !important;
    box-shadow: 0 0 10px #25AAE5;
}


/* photos */

.album.holoblock img {
	box-shadow: 0 0 5px #25AAE5;
    border-radius: 10%;
}

.album.holoblock a img {
    transition : ease-in-out 0.2s;
}

.album.holoblock a:hover img {
	box-shadow: 0 0 10px #A9EEFF;
}

/* accueil */


#myCarousel {
	display : none;
}

.home > .span12 {
    width: 97%;
    margin-top: 20px;
    margin-bottom: 20px;
	height : 250px;
    background-image: url('https://zupimages.net/up/19/50/1fy9.png');
    background-size: cover;
    background-repeat: no-repeat;
	background-position: 0 -50px;
	border: 1px solid #8FCAE0;
    animation-name: carousel;
    animation-duration: 60s;
	animation-iteration-count: infinite;
    animation-timing-function: ease-in-out;
}

@keyframes carousel {
    0% {background-image: url('https://zupimages.net/up/19/50/1fy9.png');}
    16.5% {background-image: url('https://zupimages.net/up/19/50/1fy9.png');}
    33% {background-image: url('https://zupimages.net/up/19/50/r855.png');}
    46.5% {background-image: url('https://zupimages.net/up/19/50/r855.png');}
    66% {background-image: url('https://zupimages.net/up/19/50/6xcr.png');}
    82.5% {background-image: url('https://zupimages.net/up/19/50/6xcr.png');}
    100% {background-image: url('https://zupimages.net/up/19/50/1fy9.png');}
    }



/* footer */

div.copyright {
    color: #A0A2A1;
}
}
