css:
@-moz-document domain("historykon.pl") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #c0392b #1c1c1c;
}
/*HEADER*/
.app-container header.site-brand{
    background: #c0392b;
}
.header-component{
    background: #1c1c1c !important;
}
.header-component nav.main-navigation ul li a.active{
    background: #c0392b !important;
}
.header-component nav.main-navigation ul li a{
    color: #fff;
}
.ui.input{
    color: #fff;
}
.ui.input > input{
    background: #1c1c1c;
    border: 1px solid #c0392b;
    color: #fff;
}
.ui.input > input:focus{
    background: #1c1c1c;
    color: #fff;
}
/*LEWE-MENU*/
.ui.sidebar.menu .item{
    background: #1c1c1c;
}
/*LEWE-MENU*/
/*HEADER*/
/*SEARCH*/
.category-title,.ui.items > .item .meta{
    color: #fff;
}
/*SEARCH*/
/*STRONA-GŁÓWNA*/
.historykon-container{
    background: #1c1c1c;
}
.ui.segment{
    border: 0;
    border-radius: 0;
}
.logo img{
    filter: invert(100%);
}
.basic-theme .latest-events-component div.title:hover{
    background: transparent;
    color: #fff;
}
.basic-theme .latest-events-component div.title a:hover{
    color: #fff;
}
.latest-events-component div.items > ul > li a{
    color: #fff;
}
.latest-events-component div.items > ul > li a:hover{
    color: #c0392b;
}
.ui.header{
    color: #c0392b;
}
.basic-theme .articles-grid .articles-grid-top-item figure > figcaption{
    background: #1c1c1cb8;
}
.LinesEllipsis{
    color: #c0392b;
}
.LinesEllipsis:hover{
    color: #fff;
}
.date-picker > div .calendar-card a span{
    color: #fff;
}
.fa-calendar-alt{
    color: #fff;
}
.calendarium-event .calendarium-event-caption h4 a{
    color: #fff;
}
.ui.list > .item .header{
    color: #fff;
}
.ui.list > .item .header a:hover{
    color: #c0392b;
}
.basic-theme .app-container a{
    color: #fff;
}
.basic-theme .app-container a:hover{
    color: #c0392b;
}
.trivia-widget figure figcaption p{
    color: #fff;
}
.ui.menu .item{
    color: #fff;
}
.ui.secondary.pointing.menu .active.item{
    color: #c0392b;
}
.ui.secondary.pointing.menu .active.item:hover{
    color: #fff;
}
/*NEWSLETTER*/
.ui.left.icon.input input{
    background: #1c1c1c;
    border: 1px solid #c0392b;
    color: #fff;
}
.ui.left.icon.input input::placeholder{
    color: #fff;
}
.ui.checkbox label{
    color: #fff;
}
.ui.checkbox label:hover{
    color: #fff;
}
/*NEWSLETTER*/
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.article-publication-info span{
    color: #fff;
}
.article-title{
    color: #c0392b;
}
.article-content{
    color: #fff;
}

/*WSPARCIE*/
.donate__header span{
    color: #E23F47;
}
.donate__subheader{
    color: #fff;
}
.amount{
    color: #fff;
}
.donate-button span{
    color: #fff;
}
/*WSPARCIE*/
/*ARTYKUŁ*/
/*GEOPOLITYKA*/
.rightn a,.firstn h2 a,.widget a,.rightcontp a{
    color: #771A1A;
}
.firstn,.rightn,#sidebar .widget,.homepost{
    color: #fff;
}
.widget #cat, .widget select{
    background: #1c1c1c;
    color: #fff;
}
#copyright{
    background: #1c1c1c;
    color: #fff;
}
#lang_sel_footer{
    background: #1c1c1c;
}
#lang_sel_footer li a{
    color: #fff;
}
/*ARTYKUŁ*/
.post h1 a,.post h3{
    color: #771A1A;
}
.pf-content span,.post p,ul.related_post li a{
    color: #fff !important;
}
.share_box{
    background: #1c1c1c;
}
/*ARTYKUŁ*/
/*GEOPOLITYKA*/
/*FOOTER*/
.footer,.footer .legal{
    background: #1c1c1c !important;
}
.item a{
    color: #fff !important;
}
.legal:before{
    content: "Ciemny motyw by Maciejozaman";
    color: #c0392b;
}
/*FOOTER*/
}
