css:
@-moz-document domain("film.org.pl") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #662d91 #1c1c1c;
}
/*HEADER*/
header#bottom-header{
    background: #662d91;
}
header#bottom-header nav ul li a{
    color: #fff;
}
header#bottom-header nav ul li.menu-item-has-children > ul{
    background: #662d91;
    top: 35px;
}
header#bottom-header nav ul li.menu-item-has-children > ul::before{
    background: #fff;
}
header#bottom-header .additional-menu li ul{
    background: #662d91;
    top: 35px;
}
header#bottom-header .additional-menu li ul::before{
    background: #fff;
}
header#bottom-header .additional-menu li ul li a{
    color: #fff;
}
header#bottom-header .additional-menu li i.fa.fa-search{
    color: #1c1c1c;
}
section#search form [type="text"]{
    background: #1c1c1c;
}
.fa-google-plus{
    display: none;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.homepage-news,section#authors{
    background: #1c1c1c;
}
.navigation.pagination a.page-numbers,.page-links a span{
    background: #1c1c1c;
    border: 2px solid #662d91;
}
.navigation.pagination a.page-numbers.next,.navigation.pagination a.page-numbers.prev{
    background: #1c1c1c;
}
section#authors h3{
    color: #662d91;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.white-container,.page-has-aasa section#posts-last-added{
    background: #1c1c1c;
}
.single-widget-box h2.widgettitle,.shortcode-box h3,.shortcode-text-box p,section#posts-last-added h3,.single-header-content h1,article h2,article h1,article h1 strong{
    color: #662d91;
}
article p,.single-widget-box .wpp-list li a.wpp-post-title,.shortcode-box ul li span,article strong{
    color: #fff;
}
#single .tags-row ul,.single-header-content{
    border-color: #662d91;
}
em strong{
    color: #662d91;
}
/*ARTYKUŁ*/
/*O-NAS*/
.breadcrumbs ul li a{
    color: #662d91;
}
.breadcrumbs ul li{
    color: #fff;
}
/*O-NAS*/
/*REMAKE*/
article table td, article table th{
    border-color: #662d91;
}
article a{
    color: #fff;
}
/*REMAKE*/
/*REKLAMA*/
article center{
    color: #fff;
}
/*REKLAMA*/
/*FOOTER*/
.page-has-aasa footer{
    background: #1c1c1c;
}
footer h3{
    color: #662d91;
}
footer p{
    color: #fff;
}
footer p.small-description:after{
    content: " | Ciemny motyw by Maciejozaman";
    color: #662d91;
}
/*FOOTER*/
}
